import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import CreativeWorks from './components/CreativeWorks';
import Certifications from './components/Certifications';
import Resume from './components/Resume';
import Contact from './components/Contact';
import Footer from './components/Footer';
import { BackgroundGradientAnimation } from './ui/background-gradient-animation';

const App: React.FC = () => {
  useEffect(() => {
    // Initialize EmailJS
    emailjs.init("05vg-yZOLtTcjt36N");

    // Set dark mode as default
    if (!localStorage.getItem('theme')) {
      localStorage.setItem('theme', 'dark');
    }

    const theme = localStorage.getItem('theme');
    if (theme === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  return (
    <div className="min-h-screen bg-white dark:bg-dark-950 transition-colors duration-300">
      <Header />
      <main>
        {/* Shared gradient and glass overlay for Hero + About */}
        <div className="relative">
          <div className="absolute inset-0 z-0">
            <BackgroundGradientAnimation
              gradientBackgroundStart="rgb(226, 232, 240)"
              gradientBackgroundEnd="rgb(203, 213, 225)"
              firstColor="37, 99, 235"
              secondColor="126, 34, 206"
              thirdColor="6, 182, 212"
              fourthColor="190, 24, 93"
              fifthColor="21, 128, 61"
              pointerColor="37, 99, 235"
              size="50%"
              blendingValue="soft-light"
              interactive={true}
            />
          </div>
          <div className="relative z-10">
            <section className="relative min-h-[80vh]">
              {/* Hero glass overlay, stops before transition */}
              <div className="absolute inset-0 bottom-24 z-0 pointer-events-none bg-white/80 dark:bg-dark-900/80 backdrop-blur-lg" />
              <Hero />
            </section>
            {/* Glass transition section (no overlap, exact color match) */}
            <div className="relative h-24">
              {/* Light mode glass gradient (exact match to overlays) */}
              <div
                className="absolute inset-0 z-0 pointer-events-none h-24 backdrop-blur-lg dark:hidden"
                style={{
                  background: 'linear-gradient(to bottom, rgba(255,255,255,0.8) 0%, rgba(255,255,255,0.7) 100%)'
                }}
              />
              {/* Dark mode glass gradient (exact match to overlays) */}
              <div
                className="absolute inset-0 z-0 pointer-events-none h-24 backdrop-blur-lg hidden dark:block"
                style={{
                  background: 'linear-gradient(to bottom, rgba(17,24,39,0.8) 0%, rgba(31,41,55,0.7) 100%)'
                }}
              />
            </div>
            <section className="relative min-h-[80vh]">
              {/* About glass overlay, starts after transition */}
              <div className="absolute inset-0 top-24 z-0 pointer-events-none bg-white/70 dark:bg-dark-800/70 backdrop-blur-lg" />
              <About />
            </section>
          </div>
        </div>
        {/* Other sections remain outside the shared gradient */}
        <CreativeWorks />
        <Certifications />
        <Resume />
        <Contact />
      </main>
      <Footer />
    </div>
  );
};

export default App;
