import React, { useRef, useState } from 'react';

interface GlassContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
  bgColor?: string;
  borderColor?: string;
  shadow?: string;
  cornerGlowStrength?: number;
  cornerGlowOpacity?: number;
  cornerGlowBlur?: number;
  innerShadow?: boolean;
  rounded?: string; // e.g. 'rounded-full', 'rounded-lg'
  tintColor?: string; // e.g. 'rgba(37,99,235,0.2)'
  className?: string;
}

const GlassContainer: React.FC<GlassContainerProps> = ({
  children,
  bgColor = 'rgba(241,245,249,0.7)',
  borderColor = 'rgba(226,232,240,0.4)',
  shadow = '0 4px 24px 0 rgba(30,41,59,0.10), 0 1.5px 8px 0 rgba(255,255,255,0.06), 0 0.5px 2px 0 rgba(255,255,255,0.03)',
  cornerGlowStrength = 1,
  cornerGlowOpacity = 0.5,
  cornerGlowBlur = 6,
  innerShadow = true,
  rounded = 'rounded-lg',
  tintColor = 'rgba(255,255,255,0.0)', // default: no tint
  className = '',
  ...props
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [shine, setShine] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);
  // Helper to blend white and tintColor (70% white, 30% tint)
  function blendWhiteTint(alpha: number) {
    // If tintColor is transparent, just use white
    if (!tintColor || tintColor === 'rgba(255,255,255,0.0)' || tintColor === 'transparent') {
      return `rgba(255,255,255,${alpha})`;
    }
    // Parse rgba(r,g,b,a) for tintColor
    const match = tintColor.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)/);
    if (!match) return `rgba(255,255,255,${alpha})`;
    const r = Math.round(0.7 * 255 + 0.3 * parseInt(match[1]));
    const g = Math.round(0.7 * 255 + 0.3 * parseInt(match[2]));
    const b = Math.round(0.7 * 255 + 0.3 * parseInt(match[3]));
    const a = alpha; // keep the intended alpha
    return `rgba(${r},${g},${b},${a})`;
  }
  // Corner glow alpha scaling (strength * opacity)
  const topLeftGlow = `radial-gradient(circle at -20% -20%, ${blendWhiteTint(0.36 * cornerGlowStrength * cornerGlowOpacity)} 0%, ${blendWhiteTint(0.16 * cornerGlowStrength * cornerGlowOpacity)} 60%, rgba(255,255,255,0.0) 100%)`;
  const bottomRightGlow = `radial-gradient(circle at 120% 120%, ${blendWhiteTint(0.28 * cornerGlowStrength * cornerGlowOpacity)} 0%, ${blendWhiteTint(0.12 * cornerGlowStrength * cornerGlowOpacity)} 60%, rgba(255,255,255,0.0) 100%)`;

  return (
    <div
      ref={containerRef}
      className={`relative group overflow-hidden ${rounded} focus:outline-none transition-all duration-300 ${className}`}
      style={{
        background: bgColor,
        border: `1px solid ${borderColor}`,
        boxShadow: shadow,
        backdropFilter: 'blur(20px)',
        WebkitBackdropFilter: 'blur(20px)',
        position: 'relative',
      }}
      onMouseMove={e => {
        const rect = containerRef.current?.getBoundingClientRect();
        if (rect) {
          setShine({
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
          });
        }
        setIsHovered(true);
      }}
      onMouseLeave={() => setIsHovered(false)}
      {...props}
    >
      {/* Persistent edge-hugging highlights */}
      <span
        className={`pointer-events-none absolute left-0 top-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          left: '-10px',
          top: '-10px',
          background: topLeftGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      <span
        className={`pointer-events-none absolute right-0 bottom-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          right: '-10px',
          bottom: '-10px',
          background: bottomRightGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      {/* Inner shadow (inset) */}
      {innerShadow && (
        <span
          className={`pointer-events-none absolute inset-0 ${rounded}`}
          style={{
            boxShadow: `inset 0 1.5px 8px 0 ${blendWhiteTint(0.06)}, inset 0 0.5px 2px 0 ${blendWhiteTint(0.03)}`,
            zIndex: 2,
            pointerEvents: 'none',
          }}
          aria-hidden="true"
        />
      )}
      {/* Mouse-following shine */}
      {isHovered && (
        <span
          className="pointer-events-none absolute top-0 left-0 h-full w-full"
          style={{
            background: `radial-gradient(circle at ${shine.x}px ${shine.y}px, rgba(255,255,255,0.09) 0%, rgba(255,255,255,0.04) 40%, rgba(255,255,255,0.0) 70%)`,
            filter: 'blur(2.5px)',
            zIndex: 2,
            transition: 'background 0.15s',
          }}
          aria-hidden="true"
        />
      )}
      {/* Container content */}
      <span className="relative z-20 flex items-center gap-2">{children}</span>
    </div>
  );
};

export default GlassContainer;
