import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Download, Mail } from 'lucide-react';
import GlassButton from './GlassButton';
import GlassContainer from './GlassContainer';
import GlassDebugSample from './GlassDebugSample';

const ENABLE_HERO_GLASS_OVERLAY = true;

const Hero: React.FC = () => {
  const scrollToAbout = () => {
    const element = document.querySelector('#about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Add state for hover animation
  const [isResumeHovered, setIsResumeHovered] = useState(false);
  const [isContactHovered, setIsContactHovered] = useState(false);
  const [resumeShine, setResumeShine] = useState({ x: 0, y: 0 });
  const [contactShine, setContactShine] = useState({ x: 0, y: 0 });
  const resumeBtnRef = useRef<HTMLButtonElement>(null);
  const contactBtnRef = useRef<HTMLButtonElement>(null);

  return (
    <>
      <GlassDebugSample />
      <section id="home" className="min-h-screen flex items-center justify-center relative pt-32 overflow-hidden">
        {ENABLE_HERO_GLASS_OVERLAY && (
          <div
            className="absolute inset-0 pointer-events-none z-10 bg-white/70 backdrop-blur-xl dark:bg-transparent"
            style={{
              background: document.documentElement.classList.contains('dark')
                ? 'rgba(15,23,42,0.7)'
                : undefined
            }}
          />
        )}
        {ENABLE_HERO_GLASS_OVERLAY ? (
          <div className="container-max section-padding relative z-10">
            <div className="text-center max-w-4xl mx-auto">
              {/* Profile Image */}
              <img
                src="/Profile_Image.jpg"
                alt="Alec McCutcheon profile"
                className="w-36 h-36 rounded-full mx-auto mb-8 shadow-lg border-4 border-white dark:border-dark-800 object-cover"
              />
              {/* Main Heading */}
              <motion.h1
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
                className="text-5xl md:text-7xl font-bold text-secondary-900 dark:text-white mb-6"
              >
                Hi, I'm{' '}
                <span className="gradient-text">Alec McCutcheon</span>
              </motion.h1>

              {/* Subtitle */}
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
                className="flex flex-wrap justify-center gap-3 mb-8"
              >
                {[
                  'Certified IT Specialist',
                  'PowerShell Programmer',
                  'Automation Engineer',
                  'Tier 1â€“3 Support'
                ].map((item) => (
                  <GlassContainer
                    key={item}
                    bgColor="rgba(241,245,249,0.7)"
                    borderColor="rgba(226,232,240,0.4)"
                    cornerGlowStrength={1}
                    cornerGlowOpacity={0.5}
                    cornerGlowBlur={6}
                    rounded="rounded-full"
                    className="inline-block text-primary-700 dark:text-primary-300 px-4 py-2 font-medium text-base shadow-sm dark:bg-dark-800/50 dark:border-dark-700/40 pointer-events-auto"
                    style={{ minWidth: 0 }}
                  >
                    {item}
                  </GlassContainer>
                ))}
              </motion.div>

              {/* Description */}
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.4 }}
                className="mb-12 max-w-3xl mx-auto"
              >
                <p className="text-lg text-secondary-500 dark:text-secondary-400">
                  Certified IT Specialist, seasoned PowerShell programmer, automation engineer, and experienced Tier 1â€“3 support technician. I specialize in automation, scripting, and RMM migration, developing custom solutions that streamline business operations and solve unique client challenges. My background spans system administration, technical support, and freelance work in PowerShell scripting and web design. I also have hands-on experience with network-attached storage (NAS) systemsâ€”particularly Unraidâ€”for a range of applications including Docker container orchestration, reverse proxy setup, SSL certificate management, and web server hosting.
                </p>
                <p className="text-lg text-secondary-500 dark:text-secondary-400 mt-4">
                  Outside of my professional work, Iâ€™m passionate about using technology to help othersâ€”whether itâ€™s volunteering my skills for individuals in need, building educational resources like a scareware simulation site to help users recognize online threats, or creating open-source tools to solve real-world problems.
