"use client";
import { useEffect, useRef, useState } from "react";
import { cn } from "../lib/utils";

export const BackgroundGradientAnimation = ({
  gradientBackgroundStart = "rgb(108, 0, 162)",
  gradientBackgroundEnd = "rgb(0, 17, 82)",
  firstColor = "18, 113, 255",
  secondColor = "221, 74, 255",
  thirdColor = "100, 220, 255",
  fourthColor = "200, 50, 50",
  fifthColor = "180, 180, 50",
  pointerColor = "140, 100, 255",
  size = "80%",
  blendingValue = "hard-light",
  children,
  className,
  interactive = true,
  containerClassName,
}: {
  gradientBackgroundStart?: string;
  gradientBackgroundEnd?: string;
  firstColor?: string;
  secondColor?: string;
  thirdColor?: string;
  fourthColor?: string;
  fifthColor?: string;
  pointerColor?: string;
  size?: string;
  blendingValue?: string;
  children?: React.ReactNode;
  className?: string;
  interactive?: boolean;
  containerClassName?: string;
}) => {
  const interactiveRef = useRef<HTMLDivElement>(null);
  const [curX, setCurX] = useState(0);
  const [curY, setCurY] = useState(0);
  const [tgX, setTgX] = useState(0);
  const [tgY, setTgY] = useState(0);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [dynamicPointerColor, setDynamicPointerColor] = useState(pointerColor);

  // Function to calculate color distance (RGB Euclidean distance)
  const colorDistance = (color1: string, color2: string): number => {
    const rgb1 = color1.split(',').map(c => parseInt(c.trim()));
    const rgb2 = color2.split(',').map(c => parseInt(c.trim()));
    return Math.sqrt(
      Math.pow(rgb1[0] - rgb2[0], 2) + 
      Math.pow(rgb1[1] - rgb2[1], 2) + 
      Math.pow(rgb1[2] - rgb2[2], 2)
    );
  };

  // Function to select contrasting color based on nearby gradients
  const selectContrastingColor = (mouseX: number, mouseY: number) => {
    const colors = isDarkMode ? [
      "37, 99, 235",   // first
      "126, 34, 206",  // second
      "6, 182, 212",   // third
      "190, 24, 93",   // fourth
      "21, 128, 61"    // fifth
    ] : [
      firstColor,
      secondColor,
      thirdColor,
      fourthColor,
      fifthColor
    ];

    // Calculate which region the mouse is in and find nearby colors
    const containerWidth = window.innerWidth;
    const containerHeight = window.innerHeight;

    // Define regions and their associated colors
    const regions = [
      { x: 0.2, y: 0.1, color: colors[0] },      // Top-left
      { x: 0.8, y: 0.1, color: colors[1] },      // Top-right
      { x: 0.5, y: 0.5, color: colors[2] },      // Center
      { x: 0.2, y: 0.8, color: colors[3] },      // Bottom-left
      { x: 0.8, y: 0.8, color: colors[4] },      // Bottom-right
    ];

    // Find the closest region
    let closestRegion = regions[0];
    let minDistance = Infinity;

    regions.forEach(region => {
      const distance = Math.sqrt(
        Math.pow((mouseX / containerWidth) - region.x, 2) + 
        Math.pow((mouseY / containerHeight) - region.y, 2)
      );
      if (distance < minDistance) {
        minDistance = distance;
        closestRegion = region;
      }
    });

    // Find the most contrasting color to the closest region
    let mostContrastingColor = colors[0];
    let maxContrast = 0;

    colors.forEach(color => {
      const contrast = colorDistance(closestRegion.color, color);
      if (contrast > maxContrast) {
        maxContrast = contrast;
        mostContrastingColor = color;
      }
    });

    return mostContrastingColor;
  };

  // Detect dark mode
  useEffect(() => {
    const checkDarkMode = () => {
      setIsDarkMode(document.documentElement.classList.contains('dark'));
    };

    checkDarkMode();
    const observer = new MutationObserver(checkDarkMode);
    observer.observe(document.documentElement, { attributes: true, attributeFilter: ['class'] });

    return () => observer.disconnect();
  }, []);

  // Set colors based on theme
  useEffect(() => {
    const colors = isDarkMode ? {
      bgStart: "rgb(15, 23, 42)",
      bgEnd: "rgb(2, 6, 23)",
      first: "37, 99, 235",
      second: "126, 34, 206",
      third: "6, 182, 212",
      fourth: "190, 24, 93",
      fifth: "21, 128, 61",
      pointer: "37, 99, 235"
    } : {
      bgStart: gradientBackgroundStart,
      bgEnd: gradientBackgroundEnd,
      first: firstColor,
      second: secondColor,
      third: thirdColor,
      fourth: fourthColor,
      fifth: fifthColor,
      pointer: pointerColor
    };

    document.body.style.setProperty("--gradient-background-start", colors.bgStart);
    document.body.style.setProperty("--gradient-background-end", colors.bgEnd);
    document.body.style.setProperty("--first-color", colors.first);
    document.body.style.setProperty("--second-color", colors.second);
    document.body.style.setProperty("--third-color", colors.third);
    document.body.style.setProperty("--fourth-color", colors.fourth);
    document.body.style.setProperty("--fifth-color", colors.fifth);
    document.body.style.setProperty("--pointer-color", colors.pointer);
    document.body.style.setProperty("--size", size);
    document.body.style.setProperty("--blending-value", blendingValue);
  }, [isDarkMode, gradientBackgroundStart, gradientBackgroundEnd, firstColor, secondColor, thirdColor, fourthColor, fifthColor, pointerColor, size, blendingValue]);

  useEffect(() => {
    function move() {
      if (!interactiveRef.current) {
        return;
      }
      setCurX(curX + (tgX - curX) / 20);
      setCurY(curY + (tgY - curY) / 20);
      interactiveRef.current.style.transform = `translate(${Math.round(
        curX
      )}px, ${Math.round(curY)}px)`;
    }
    move();
  }, [tgX, tgY, curX, curY]);

  const handleMouseMove = (event: React.MouseEvent<HTMLDivElement>) => {
    console.log('Mouse move detected:', event.clientX, event.clientY);
    const rect = event.currentTarget.getBoundingClientRect();
    setTgX(event.clientX - rect.left);
    setTgY(event.clientY - rect.top);

    // Update dynamic color based on mouse position
    const contrastingColor = selectContrastingColor(event.clientX, event.clientY);
    setDynamicPointerColor(contrastingColor);
  };

  const [isSafari, setIsSafari] = useState(false);
  useEffect(() => {
    setIsSafari(/^((?!chrome|android).)*safari/i.test(navigator.userAgent));
  }, []);

  return (
    <div
      className={cn(
        "h-full w-full relative overflow-hidden top-0 left-0 bg-[linear-gradient(40deg,var(--gradient-background-start),var(--gradient-background-end))]",
        containerClassName
      )}
      onMouseMove={interactive ? handleMouseMove : undefined}
    >
      <svg className="hidden">
        <defs>
          <filter id="blurMe">
            <feGaussianBlur in="SourceGraphic" stdDeviation="10" result="blur" />
            <feColorMatrix
              in="blur"
