import React, { useRef, useState, useEffect, useLayoutEffect, useContext } from 'react';
import { useBackgroundColorsAtPoints } from '../hooks/useBackgroundColorsAtPoints';
import { BackgroundColorContext } from '../ui/background-gradient-animation';

interface GlassContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
  bgColor?: string;
  borderColor?: string;
  shadow?: string;
  cornerGlowStrength?: number;
  cornerGlowOpacity?: number;
  cornerGlowBlur?: number;
  innerShadow?: boolean;
  rounded?: string; // e.g. 'rounded-full', 'rounded-lg'
  tintColor?: string; // e.g. 'rgba(37,99,235,0.2)'
  className?: string;
}

const GlassContainer: React.FC<GlassContainerProps> = ({
  children,
  bgColor = 'rgba(241,245,249,0.7)',
  borderColor = 'rgba(226,232,240,0.4)',
  shadow = '0 4px 24px 0 rgba(30,41,59,0.10), 0 1.5px 8px 0 rgba(255,255,255,0.06), 0 0.5px 2px 0 rgba(255,255,255,0.03)',
  cornerGlowStrength = 1,
  cornerGlowOpacity = 0.5,
  cornerGlowBlur = 6,
  innerShadow = true,
  rounded = 'rounded-lg',
  tintColor,
  className = '',
  ...props
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [shine, setShine] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);
  // Detect dark mode
  const [isDarkMode, setIsDarkMode] = useState(false);
  useEffect(() => {
    const checkDark = () => setIsDarkMode(document.documentElement.classList.contains('dark'));
    checkDark();
    const observer = new MutationObserver(checkDark);
    observer.observe(document.documentElement, { attributes: true, attributeFilter: ['class'] });
    return () => observer.disconnect();
  }, []);

  // Use background color context for color-aware tinting
  const bgColorContext = useContext(BackgroundColorContext);
  // Sample background color at eight unique edge points: two per edge (quarter and three-quarters along each edge)
  const [edgePoints, setEdgePoints] = useState<{x: number, y: number}[]>([
    {x: 0, y: 0}, // top-left quarter
    {x: 0, y: 0}, // top-right quarter
    {x: 0, y: 0}, // right-top quarter
    {x: 0, y: 0}, // right-bottom quarter
    {x: 0, y: 0}, // bottom-right quarter
    {x: 0, y: 0}, // bottom-left quarter
    {x: 0, y: 0}, // left-bottom quarter
    {x: 0, y: 0}, // left-top quarter
  ]);
  useLayoutEffect(() => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    setEdgePoints([
      // Top edge: 1/4 and 3/4
      { x: rect.left + rect.width * 0.25, y: rect.top + 4 },
      { x: rect.left + rect.width * 0.75, y: rect.top + 4 },
      // Right edge: 1/4 and 3/4
      { x: rect.right - 4, y: rect.top + rect.height * 0.25 },
      { x: rect.right - 4, y: rect.top + rect.height * 0.75 },
      // Bottom edge: 3/4 and 1/4 (to keep order consistent)
      { x: rect.left + rect.width * 0.75, y: rect.bottom - 4 },
      { x: rect.left + rect.width * 0.25, y: rect.bottom - 4 },
      // Left edge: 3/4 and 1/4
      { x: rect.left + 4, y: rect.top + rect.height * 0.75 },
      { x: rect.left + 4, y: rect.top + rect.height * 0.25 },
    ]);
  }, [children, className, rounded]);
  const [
    autoTintTopLeftQuarter,
    autoTintTopRightQuarter,
    autoTintRightTopQuarter,
    autoTintRightBottomQuarter,
    autoTintBottomRightQuarter,
    autoTintBottomLeftQuarter,
    autoTintLeftBottomQuarter,
    autoTintLeftTopQuarter
  ] = useBackgroundColorsAtPoints(edgePoints, 60);

  function blendWhiteTint(alpha: number, tintOverride?: string) {
    let tint = tintOverride ?? tintColor;
    if (!tint || tint === 'rgba(255,255,255,0.0)' || tint === 'transparent') {
      // Use a slightly brighter version of the background color as fallback
      // Try to parse bgColor as rgb/rgba, otherwise fallback to white
      const match = bgColor.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)/);
      if (match) {
        // Brighten the background color by blending with white (70% bg, 30% white)
        const r = Math.round(0.7 * parseInt(match[1]) + 0.3 * 255);
        const g = Math.round(0.7 * parseInt(match[2]) + 0.3 * 255);
        const b = Math.round(0.7 * parseInt(match[3]) + 0.3 * 255);
        return `rgba(${r},${g},${b},${alpha})`;
      }
      return `rgba(255,255,255,${alpha})`;
    }
    // Parse rgb(a) for tintColor
    const match = tint.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)/);
    if (!match) return `rgba(255,255,255,${alpha})`;
    // 20% white, 80% tint for much more prominent color
    const r = Math.round(0.2 * 255 + 0.8 * parseInt(match[1]));
    const g = Math.round(0.2 * 255 + 0.8 * parseInt(match[2]));
    const b = Math.round(0.2 * 255 + 0.8 * parseInt(match[3]));
    const a = alpha;
    return `rgba(${r},${g},${b},${a})`;
  }
  // Corner glow alpha scaling (strength * opacity)
  const topLeftGlow = `radial-gradient(circle at -20% -20%, ${blendWhiteTint(2.2 * cornerGlowStrength * cornerGlowOpacity, autoTintTopLeftQuarter)} 0%, ${blendWhiteTint(1.5 * cornerGlowStrength * cornerGlowOpacity, autoTintTopLeftQuarter)} 60%, rgba(255,255,255,0.0) 100%)`;
  const bottomRightGlow = `radial-gradient(circle at 120% 120%, ${blendWhiteTint(2.2 * cornerGlowStrength * cornerGlowOpacity, autoTintBottomRightQuarter)} 0%, ${blendWhiteTint(1.5 * cornerGlowStrength * cornerGlowOpacity, autoTintBottomRightQuarter)} 60%, rgba(255,255,255,0.0) 100%)`;

  // Debug: log tint settings while hovered
  useEffect(() => {
    let interval: NodeJS.Timeout | undefined;
    if (isHovered) {
      const log = () => {
        // Log the sampled tints and computed values for each section
        // eslint-disable-next-line no-console
        console.log('[GlassContainer] isHovered:', {
          isDarkMode,
          tintColor,
          autoTintTopLeftQuarter,
          autoTintTopRightQuarter,
          autoTintRightTopQuarter,
          autoTintRightBottomQuarter,
          autoTintBottomRightQuarter,
          autoTintBottomLeftQuarter,
          autoTintLeftBottomQuarter,
          autoTintLeftTopQuarter,
          blendWhiteTintTopLeftQuarter: blendWhiteTint(0.36, autoTintTopLeftQuarter),
          blendWhiteTintTopRightQuarter: blendWhiteTint(0.36, autoTintTopRightQuarter),
          blendWhiteTintRightTopQuarter: blendWhiteTint(0.36, autoTintRightTopQuarter),
          blendWhiteTintRightBottomQuarter: blendWhiteTint(0.36, autoTintRightBottomQuarter),
          blendWhiteTintBottomRightQuarter: blendWhiteTint(0.36, autoTintBottomRightQuarter),
          blendWhiteTintBottomLeftQuarter: blendWhiteTint(0.36, autoTintBottomLeftQuarter),
          blendWhiteTintLeftBottomQuarter: blendWhiteTint(0.36, autoTintLeftBottomQuarter),
          blendWhiteTintLeftTopQuarter: blendWhiteTint(0.36, autoTintLeftTopQuarter),
          boxShadow: `inset 0 1.5px 8px 0 ${blendWhiteTint(0.06, autoTintTopLeftQuarter)}, inset 0 0.5px 2px 0 ${blendWhiteTint(0.03, autoTintBottomRightQuarter)}`,
        });
      };
      log();
      interval = setInterval(log, 1000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isHovered, tintColor, isDarkMode, autoTintTopLeftQuarter, autoTintTopRightQuarter, autoTintRightTopQuarter, autoTintRightBottomQuarter, autoTintBottomRightQuarter, autoTintBottomLeftQuarter, autoTintLeftBottomQuarter, autoTintLeftTopQuarter]);

  // Use the edge tints for the corresponding edge of the inner shadow
  // For the boxShadow, use the edge tints for each edge
  // Top: autoTintTop, Right: autoTintRight, Bottom: autoTintBottom, Left: autoTintLeft
  // Corners can still use autoTintTopLeft and autoTintBottomRight for the glows

  // Example boxShadow for two per edge (eight total):
  const edgeShadow = `
    inset 0 2px 8px 0 ${blendWhiteTint(0.18, autoTintTopLeftQuarter)},
    inset 0 2px 8px 0 ${blendWhiteTint(0.18, autoTintTopRightQuarter)},
    inset -2px 0 8px 0 ${blendWhiteTint(0.18, autoTintRightTopQuarter)},
    inset -2px 0 8px 0 ${blendWhiteTint(0.18, autoTintRightBottomQuarter)},
    inset 0 -2px 8px 0 ${blendWhiteTint(0.18, autoTintBottomRightQuarter)},
    inset 0 -2px 8px 0 ${blendWhiteTint(0.18, autoTintBottomLeftQuarter)},
    inset 2px 0 8px 0 ${blendWhiteTint(0.18, autoTintLeftBottomQuarter)},
    inset 2px 0 8px 0 ${blendWhiteTint(0.18, autoTintLeftTopQuarter)}
  `;

  // Track the sampled background color at the mouse position for the shine effect
  const [shineBgColor, setShineBgColor] = useState('rgb(255,255,255)');
  useEffect(() => {
    if (!isHovered) return;
    const getColor = (window as any).__getBackgroundColorAt;
    if (!getColor) return;
    let running = true;
    function updateColor() {
      if (!running) return;
      setShineBgColor(getColor(shine.x + (containerRef.current?.getBoundingClientRect().left || 0), shine.y + (containerRef.current?.getBoundingClientRect().top || 0)));
      requestAnimationFrame(updateColor);
    }
    updateColor();
    return () => { running = false; };
  }, [isHovered, shine.x, shine.y]);

  // Helper to blend two rgb colors as strings
  function blendRgbStrings(rgbA: string, rgbB: string, t: number) {
    const parse = (s: string) => s.match(/\d+/g)?.map(Number) || [255,255,255];
    const a = parse(rgbA), b = parse(rgbB);
    return `rgb(${Math.round(a[0]*(1-t)+b[0]*t)},${Math.round(a[1]*(1-t)+b[1]*t)},${Math.round(a[2]*(1-t)+b[2]*t)})`;
  }

  return (
    <div
      ref={containerRef}
      className={`relative group overflow-hidden ${rounded} focus:outline-none transition-all duration-300 ${className}`}
      style={{
        background: bgColor,
        border: `1px solid ${borderColor}`,
        boxShadow: shadow,
        backdropFilter: 'blur(20px)',
        WebkitBackdropFilter: 'blur(20px)',
        position: 'relative',
      }}
      onMouseMove={e => {
        const rect = containerRef.current?.getBoundingClientRect();
        if (rect) {
          setShine({
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
          });
        }
        setIsHovered(true);
      }}
      onMouseLeave={() => setIsHovered(false)}
      {...props}
    >
      {/* Persistent edge-hugging highlights */}
      <span
        className={`pointer-events-none absolute left-0 top-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          left: '-10px',
          top: '-10px',
          background: topLeftGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      <span
        className={`pointer-events-none absolute right-0 bottom-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          right: '-10px',
          bottom: '-10px',
          background: bottomRightGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      {/* Inner shadow (inset) */}
      {innerShadow && (
        <>
          <span
            className={`pointer-events-none absolute inset-0 ${rounded}`}
            style={{
              boxShadow: edgeShadow,
              zIndex: 2,
              pointerEvents: 'none',
            }}
            aria-hidden="true"
          />
          <span
            className={`pointer-events-none absolute inset-0 ${rounded}`}
            style={{
              boxShadow: edgeShadow,
              zIndex: 2,
              pointerEvents: 'none',
            }}
            aria-hidden="true"
          />
        </>
      )}
      {/* Mouse-following shine */}
      {isHovered && (
        <span
          className="pointer-events-none absolute top-0 left-0 h-full w-full"
          style={{
            background: `radial-gradient(circle at ${shine.x}px ${shine.y}px, ${blendRgbStrings('rgb(255,255,255)', shineBgColor, 0.3).replace('rgb', 'rgba').replace(')', ',0.09)')} 0%, ${blendRgbStrings('rgb(255,255,255)', shineBgColor, 0.3).replace('rgb', 'rgba').replace(')', ',0.04)')} 20%, rgba(255,255,255,0.0) 35%)`,
            filter: 'blur(2.5px)',
            zIndex: 2,
            transition: 'background 0.15s',
          }}
          aria-hidden="true"
        />
      )}
      {/* DEBUG: Render dots at the sampling points with their sampled color */}
      {/*
      <div style={{ position: 'fixed', left: edgePoints[0].x - 6, top: edgePoints[0].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintTopLeftQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[1].x - 6, top: edgePoints[1].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintTopRightQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[2].x - 6, top: edgePoints[2].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintRightTopQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[3].x - 6, top: edgePoints[3].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintRightBottomQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[4].x - 6, top: edgePoints[4].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintBottomRightQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[5].x - 6, top: edgePoints[5].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintBottomLeftQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[6].x - 6, top: edgePoints[6].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintLeftBottomQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      <div style={{ position: 'fixed', left: edgePoints[7].x - 6, top: edgePoints[7].y - 6, width: 12, height: 12, borderRadius: 6, background: autoTintLeftTopQuarter, border: '2px solid black', zIndex: 99999, pointerEvents: 'none', boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)' }} />
      */}
      {/* Container content */}
      <span className="relative z-20 flex items-center gap-2">{children}</span>
    </div>
  );
};

export default GlassContainer;
