import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Download, Mail } from 'lucide-react';

const Hero: React.FC = () => {
  const scrollToAbout = () => {
    const element = document.querySelector('#about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Add state for hover animation
  const [isResumeHovered, setIsResumeHovered] = useState(false);
  const [isContactHovered, setIsContactHovered] = useState(false);
  const [resumeShine, setResumeShine] = useState({ x: 0, y: 0 });
  const [contactShine, setContactShine] = useState({ x: 0, y: 0 });
  const resumeBtnRef = useRef<HTMLButtonElement>(null);
  const contactBtnRef = useRef<HTMLButtonElement>(null);

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative pt-32 overflow-hidden">
      <div className="container-max section-padding relative z-10">
        <div className="text-center max-w-4xl mx-auto">
          {/* Profile Image */}
          <img
            src="/Profile_Image.jpg"
            alt="Alec McCutcheon profile"
            className="w-36 h-36 rounded-full mx-auto mb-8 shadow-lg border-4 border-white dark:border-dark-800 object-cover"
          />
          {/* Main Heading */}
          <motion.h1
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-5xl md:text-7xl font-bold text-secondary-900 dark:text-white mb-6"
          >
            Hi, I'm{' '}
            <span className="gradient-text">Alec McCutcheon</span>
          </motion.h1>

          {/* Subtitle */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="flex flex-wrap justify-center gap-3 mb-8"
          >
            {[
              'Certified IT Specialist',
              'PowerShell Programmer',
              'Automation Engineer',
              'Tier 1–3 Support'
            ].map((item) => (
              <span
                key={item}
                className="inline-block bg-secondary-100/50 dark:bg-dark-800/50 backdrop-blur border border-secondary-200/40 dark:border-dark-700/40 text-primary-700 dark:text-primary-300 px-4 py-2 rounded-full font-medium text-base shadow-sm"
              >
                {item}
              </span>
            ))}
          </motion.div>

          {/* Description */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="mb-12 max-w-3xl mx-auto"
          >
            <p className="text-lg text-secondary-500 dark:text-secondary-400">
              Certified IT Specialist, seasoned PowerShell programmer, automation engineer, and experienced Tier 1–3 support technician. I specialize in automation, scripting, and RMM migration, developing custom solutions that streamline business operations and solve unique client challenges. My background spans system administration, technical support, and freelance work in PowerShell scripting and web design. I also have hands-on experience with network-attached storage (NAS) systems—particularly Unraid—for a range of applications including Docker container orchestration, reverse proxy setup, SSL certificate management, and web server hosting.
            </p>
            <p className="text-lg text-secondary-500 dark:text-secondary-400 mt-4">
              Outside of my professional work, I’m passionate about using technology to help others—whether it’s volunteering my skills for individuals in need, building educational resources like a scareware simulation site to help users recognize online threats, or creating open-source tools to solve real-world problems.
            </p>
            <p className="text-lg text-secondary-500 dark:text-secondary-400 mt-4">
              I’m always exploring new technologies—dabbling in React, Flutter, and integrating AI into my workflow—to stay at the forefront of innovation and to empower others through knowledge and practical solutions.
            </p>
          </motion.div>

          {/* CTA Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16"
          >
            <button
              ref={resumeBtnRef}
              className="flex items-center gap-2 bg-primary-600/70 dark:bg-primary-700/40 text-white backdrop-blur-xl border border-primary-200/40 dark:border-primary-900/40 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300 transition-shadow relative group overflow-hidden pointer-events-auto py-3 px-6 rounded-lg font-medium focus:outline-none focus:ring-0"
              style={{ boxShadow: 'inset 0 1.5px 8px 0 rgba(255,255,255,0.10), inset 0 0.5px 2px 0 rgba(255,255,255,0.07)' }}
              onMouseMove={e => {
                const rect = resumeBtnRef.current?.getBoundingClientRect();
                if (rect) {
                  setResumeShine({
                    x: e.clientX - rect.left,
                    y: e.clientY - rect.top
                  });
                }
                setIsResumeHovered(true);
              }}
              onMouseLeave={() => setIsResumeHovered(false)}
            >
              {/* Persistent inner glows */}
              {/* Top-left corner glow */}
              <span
                className="pointer-events-none absolute left-0 top-0 w-11 h-11"
                style={{
                  background: 'radial-gradient(circle at 30% 30%, rgba(255,255,255,0.18) 0%, rgba(255,255,255,0.08) 60%, rgba(255,255,255,0.0) 100%)',
                  filter: 'blur(2.5px)',
                  zIndex: 1,
                  pointerEvents: 'none',
                }}
                aria-hidden="true"
              />
              {/* Bottom-right corner glow */}
              <span
                className="pointer-events-none absolute right-0 bottom-0 w-11 h-11"
                style={{
                  background: 'radial-gradient(circle at 70% 70%, rgba(255,255,255,0.13) 0%, rgba(255,255,255,0.06) 60%, rgba(255,255,255,0.0) 100%)',
                  filter: 'blur(2.5px)',
                  zIndex: 1,
                  pointerEvents: 'none',
                }}
                aria-hidden="true"
              />
              {/* Mouse-following shine */}
              {isResumeHovered && (
                <span
                  className="pointer-events-none absolute top-0 left-0 h-full w-full"
                  style={{
                    background: `radial-gradient(circle at ${resumeShine.x}px ${resumeShine.y}px, rgba(255,255,255,0.18) 0%, rgba(255,255,255,0.09) 40%, rgba(255,255,255,0.0) 70%)`,
                    filter: 'blur(2.5px)',
                    zIndex: 2,
                    transition: 'background 0.15s',
                  }}
                  aria-hidden="true"
                />
              )}
              <Download size={20} />
              Download Resume
            </button>
            <button
              ref={contactBtnRef}
              type="button"
              className="flex items-center gap-2 bg-secondary-100/70 dark:bg-dark-800/40 text-secondary-800 dark:text-white backdrop-blur-xl border border-secondary-200/40 dark:border-dark-700/40 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300 transition-shadow relative group overflow-hidden pointer-events-auto py-3 px-6 rounded-lg font-medium focus:outline-none focus:ring-0"
              style={{ boxShadow: 'inset 0 1.5px 8px 0 rgba(255,255,255,0.10), inset 0 0.5px 2px 0 rgba(255,255,255,0.07)' }}
              onMouseMove={e => {
                const rect = contactBtnRef.current?.getBoundingClientRect();
                if (rect) {
                  setContactShine({
                    x: e.clientX - rect.left,
                    y: e.clientY - rect.top
                  });
                }
                setIsContactHovered(true);
              }}
              onMouseLeave={() => setIsContactHovered(false)}
              onClick={() => {
                const el = document.querySelector('#contact');
                if (el) el.scrollIntoView({ behavior: 'smooth' });
              }}
            >
              {/* Persistent inner glows */}
              {/* Top-left corner glow */}
              <span
                className="pointer-events-none absolute left-0 top-0 w-11 h-11"
                style={{
                  background: 'radial-gradient(circle at 30% 30%, rgba(255,255,255,0.18) 0%, rgba(255,255,255,0.08) 60%, rgba(255,255,255,0.0) 100%)',
                  filter: 'blur(2.5px)',
                  zIndex: 1,
                  pointerEvents: 'none',
                }}
                aria-hidden="true"
              />
              {/* Bottom-right corner glow */}
              <span
                className="pointer-events-none absolute right-0 bottom-0 w-11 h-11"
                style={{
                  background: 'radial-gradient(circle at 70% 70%, rgba(255,255,255,0.13) 0%, rgba(255,255,255,0.06) 60%, rgba(255,255,255,0.0) 100%)',
                  filter: 'blur(2.5px)',
                  zIndex: 1,
                  pointerEvents: 'none',
                }}
                aria-hidden="true"
              />
              {/* Mouse-following shine */}
              {isContactHovered && (
                <span
                  className="pointer-events-none absolute top-0 left-0 h-full w-full"
                  style={{
                    background: `radial-gradient(circle at ${contactShine.x}px ${contactShine.y}px, rgba(255,255,255,0.18) 0%, rgba(255,255,255,0.09) 40%, rgba(255,255,255,0.0) 70%)`,
                    filter: 'blur(2.5px)',
                    zIndex: 2,
                    transition: 'background 0.15s',
                  }}
                  aria-hidden="true"
                />
              )}
              <Mail size={20} />
              Get In Touch
            </button>
          </motion.div>

          {/* Scroll Indicator */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 1 }}
            className="flex justify-center"
          >
            <button
              onClick={scrollToAbout}
              className="flex flex-col items-center text-secondary-400 dark:text-secondary-500 hover:text-primary-600 dark:hover:text-primary-400 transition-colors duration-200"
            >
              <span className="text-sm mb-2">Learn More</span>
              <motion.div
                animate={{ y: [0, 10, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <ChevronDown size={24} />
              </motion.div>
            </button>
          </motion.div>
        </div>
      </div>
