export type ContactInfo = {
  name: string;
  address?: string;
  phone?: string;
  email?: string;
  website?: string;
  location?: string;
};

export type HeroInfo = {
  headline?: string; // tagline under name
  summary?: string;  // short paragraph summary
};

export type ExperienceItem = {
  title: string;
  company: string;
  location?: string;
  period?: string; // e.g., "Jul 2019 – 2024"
  bullets: string[];
};

export type EducationItem = {
  degree: string;
  institution: string;
  location?: string;
  period?: string;
  description?: string;
};

export type ResumeData = {
  contact: ContactInfo;
  hero?: HeroInfo;
  experience?: ExperienceItem[];
  education?: EducationItem[];
  skills?: string[]; // simple list
};

export type ExportOptions = {
  fileName?: string; // default derived from name
  page?: 'letter' | 'a4';
  autoDownload?: boolean; // true = use html2pdf (if available); false = open print dialog
};

// --- Utilities ---
export function escapeMd(text: string | undefined): string {
  if (!text) return '';
  return text.replace(/[<>*_`~|]/g, (m) => `\\${m}`);
}

export function generateResumeMarkdown(data: ResumeData): string {
  const name = escapeMd(data.contact.name);
  const headline = escapeMd(data.hero?.headline);
  const summary = escapeMd(data.hero?.summary);
  const address = escapeMd(data.contact.address || data.contact.location);
  const phone = escapeMd(data.contact.phone);
  const email = escapeMd(data.contact.email);
  const website = escapeMd(data.contact.website);

  const lines: string[] = [];
  lines.push(`# ${name}`);
  lines.push('');
  // Two-column header simulated with table
  lines.push('|  |  |');
  lines.push('|---|---|');
  lines.push(`| ${summary || ''} | ${[address, phone, email, website].filter(Boolean).join('<br/>')} |`);
  lines.push('');

  if (data.experience && data.experience.length) {
    lines.push('## Employment history');
    lines.push('');
    for (const exp of data.experience) {
      const title = escapeMd(exp.title);
      const company = escapeMd(exp.company);
      const location = escapeMd(exp.location);
      const period = escapeMd(exp.period);
      lines.push(`### ${title}`);
      lines.push(`_${company}${location ? ' at ' + location : ''}_  ${period ? ' · ' + period : ''}`);
      lines.push('');
      for (const b of exp.bullets) {
        lines.push(`- ${escapeMd(b)}`);
      }
      lines.push('');
    }
  }

  if (data.education && data.education.length) {
    lines.push('## Education');
    lines.push('');
    for (const ed of data.education) {
      const degree = escapeMd(ed.degree);
      const inst = escapeMd(ed.institution);
      const location = escapeMd(ed.location);
      const period = escapeMd(ed.period);
      lines.push(`### ${degree}`);
      lines.push(`_${inst}${location ? ' · ' + location : ''}_  ${period ? ' · ' + period : ''}`);
      if (ed.description) {
        lines.push('');
        lines.push(escapeMd(ed.description));
      }
      lines.push('');
    }
  }

  if (data.skills && data.skills.length) {
    lines.push('## Skills');
    lines.push('');
    lines.push(data.skills.map(s => `
- ${escapeMd(s)}`).join(''));
    lines.push('');
  }

  return lines.join('\n');
}

// Minimal MD -> HTML (headings, italics, list, table line breaks)
export function markdownToHtml(md: string): string {
  let html = md;
  html = html.replace(/^### (.*)$/gm, '<h3>$1</h3>');
  html = html.replace(/^## (.*)$/gm, '<h2>$1</h2>');
  html = html.replace(/^# (.*)$/gm, '<h1>$1</h1>');
  html = html.replace(/^\- (.*)$/gm, '<li>$1</li>');
  // OLD CODE - KEEP UNTIL CONFIRMED WORKING
  // html = html.replace(/\n\n/g, '</p><p>');
  // html = html.replace(/\n/g, '<br/>');
  // NEW CODE - TESTING: simpler breaks to avoid invalid <p> nesting around headings/lists
  html = html.replace(/\n{2,}/g, '<br/><br/>' );
  html = html.replace(/\n/g, '<br/>' );
  // Basic tables
  html = html.replace(/\|\s*\|\s*\|/g, '|  |  |');
  // Wrap lists (avoid dotAll 's' flag for older targets)
  html = html.replace(/(<li>[\s\S]*<\/li>)/g, '<ul>$1</ul>');
  // OLD CODE - KEEP UNTIL CONFIRMED WORKING
  // return `<p>${html}</p>`;
  // NEW CODE - TESTING: return raw HTML body without wrapping in <p>
  return `${html}`;
}

// NEW CODE - TESTING: direct HTML builder for consistent layout in PDF
export function buildResumeHtml(data: ResumeData): string {
  const safe = (v?: string) => v ? v : '';
  const esc = (v?: string) => v ? v.replace(/[&<>]/g, (m) => ({'&':'&amp;','<':'&lt;','>':'&gt;'} as any)[m]) : '';

  const contactLines: string[] = [];
  if (data.contact.address || data.contact.location) contactLines.push(esc(data.contact.address || data.contact.location));
  if (data.contact.phone) contactLines.push(`<a href="tel:${esc(data.contact.phone)}">${esc(data.contact.phone)}</a>`);
  if (data.contact.email) contactLines.push(`<a href="mailto:${esc(data.contact.email)}">${esc(data.contact.email)}</a>`);
  if (data.contact.website) contactLines.push(`<a href="${esc(data.contact.website)}" target="_blank" rel="noopener">${esc(data.contact.website)}</a>`);

  const expHtml = (data.experience || []).map(exp => `
    <section class="exp">
      <h3>${esc(exp.title)}</h3>
      <div class="meta"><em>${esc(exp.company)}${exp.location ? ' · ' + esc(exp.location) : ''}</em>${exp.period ? ` · ${esc(exp.period)}` : ''}</div>
      ${exp.bullets && exp.bullets.length ? `<ul>${exp.bullets.map(b => `<li>${esc(b)}</li>`).join('')}</ul>` : ''}
    </section>
  `).join('');

  const eduHtml = (data.education || []).map(ed => `
    <section class="edu">
      <h3>${esc(ed.degree)}</h3>
      <div class="meta"><em>${esc(ed.institution)}${ed.location ? ' · ' + esc(ed.location) : ''}</em>${ed.period ? ` · ${esc(ed.period)}` : ''}</div>
      ${ed.description ? `<p>${esc(ed.description)}</p>` : ''}
    </section>
  `).join('');

  const skillsHtml = (data.skills && data.skills.length)
    ? `<ul class="skills-list">${data.skills.map(s => `<li>${esc(s)}</li>`).join('')}</ul>`
    : '';

  return `
  <div class="resume-export-root">
    <header>
      <h1>${esc(data.contact.name)}</h1>
      <div class="header-grid">
        <div class="summary">${esc(data.hero?.summary)}</div>
        <div class="contact">${contactLines.join('<br/>')}</div>
      </div>
    </header>
    ${expHtml ? `<h2>Employment history</h2>${expHtml}` : ''}
    ${eduHtml ? `<h2>Education</h2>${eduHtml}` : ''}
    ${skillsHtml ? `<h2>Skills</h2>${skillsHtml}` : ''}
  </div>`;
}

