import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Download, Briefcase, GraduationCap, Star, Calendar, MapPin } from 'lucide-react';

interface Experience {
  id: number;
  title: string;
  company: string;
  location: string;
  period: string;
  description: string[];
  technologies: string[];
}

interface Education {
  id: number;
  degree: string;
  institution: string;
  location: string;
  period: string;
  description: string;
}

const Resume: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [activeTab, setActiveTab] = useState<'experience' | 'education' | 'skills'>('experience');

  const experiences: Experience[] = [
    {
      id: 1,
      title: "IT Support Technician",
      company: "One Bridge Consulting",
      location: "Monmouth, ME",
      period: "May 2019 - Present",
      description: [
        "Designed and developed the company's new website using HTML, CSS, JavaScript, and web development platforms",
        "Created custom PowerShell scripts for system automation, vulnerability assessment, and hardware/warranty information gathering",
        "Provided Tier 1 and Tier 2 technical support for multiple clients with customized solutions",
        "Automated machine migration to Azure AD and implemented system health monitoring and file corruption repair",
        "Developed advanced storage/usage metrics and log data gathering systems for improved system performance"
      ],
      technologies: ["PowerShell", "HTML/CSS/JavaScript", "Azure AD", "ConnectWise Automate", "System Administration", "Web Development"]
    },
    {
      id: 2,
      title: "PowerShell Programmer/Consultant",
      company: "Fiverr (Freelance)",
      location: "Remote",
      period: "Feb 2023 - Present",
      description: [
        "Providing freelance PowerShell scripting and consulting services",
        "Writing, optimizing, and consulting for PowerShell projects",
        "Delivering customized automation solutions for clients worldwide",
        "Maintaining high client satisfaction through quality deliverables"
      ],
      technologies: ["PowerShell", "Automation", "Scripting", "System Administration", "Consulting"]
    },
    {
      id: 3,
      title: "Freelance/Volunteer IT Consultant",
      company: "Volunteer Services",
      location: "Maine, United States",
      period: "2017 - Present",
      description: [
        "Providing technical support and IT consulting services to community members",
        "Creating websites, establishing networks, and addressing hardware/software issues",
        "Composing scripts and providing user education and guidance",
        "Maintaining passion for technology while helping others in a voluntary capacity"
      ],
      technologies: ["IT Support", "Web Design", "Networking", "Scripting", "Technical Support"]
    },
    {
      id: 4,
      title: "Freelance Web Designer",
      company: "McCutcheon's Health Products",
      location: "Hybrid",
      period: "Dec 2018 - Feb 2019",
      description: [
        "Successfully designed and deployed a website for the organization",
        "Delivered a visually appealing, user-friendly site that conveyed brand messaging",
        "Utilized SEO best practices to maximize online visibility",
        "Ensured potential customers could easily locate the site online"
      ],
      technologies: ["Web Design", "SEO", "CSS", "HTML", "User Experience", "Brand Messaging"]
    }
  ];

  const education: Education[] = [
    {
      id: 1,
      degree: "Information Technology",
      institution: "Mid-Maine Technical Center",
      location: "Maine",
      period: "2016 - 2017",
      description: "Basic troubleshooting of desktop and laptop hardware and software problems using diagnostic software and electronic test equipment. Able to assemble, upgrade, and install computer operating systems, perform preventive maintenance, and conduct diagnostic system tests."
    },
    {
      id: 2,
      degree: "Apple Technology",
      institution: "Mid-Maine Technical Center",
      location: "Maine",
      period: "2017 - 2018",
      description: "Basic troubleshooting and repair of desktop and portable Macintosh systems. Able to identify and resolve common OS X problems and use Apple Service and Support products to effectively repair Apple hardware."
    },
    {
      id: 3,
      degree: "High School Diploma",
      institution: "Messalonskee High School",
      location: "Maine",
      period: "2014 - 2018",
      description: "Completed high school education with focus on technology and computer systems."
    }
  ];

  const skillCategories = [
    {
      category: "System Administration",
      skills: [
        { name: "PowerShell", level: 95 },
        { name: "System Administration", level: 90 },
        { name: "Hardware Troubleshooting", level: 90 },
        { name: "Network Setup", level: 85 },
        { name: "Azure AD", level: 85 }
      ]
    },
    {
      category: "Web Development",
      skills: [
        { name: "HTML/CSS", level: 90 },
        { name: "JavaScript", level: 85 },
        { name: "Web Design", level: 85 },
        { name: "SEO", level: 80 },
        { name: "User Experience", level: 80 }
      ]
    },
    {
      category: "IT Support & Tools",
      skills: [
        { name: "ConnectWise Automate", level: 90 },
        { name: "Malware Detection", level: 85 },
        { name: "System Hardening", level: 85 },
        { name: "Automation Scripting", level: 95 },
        { name: "Technical Support", level: 90 }
      ]
    }
  ];

  return (
    <section id="resume" className="section-padding bg-secondary-50 dark:bg-dark-900">
      <div className="container-max">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-secondary-900 dark:text-white mb-4">
            Resume
          </h2>
          <p className="text-xl text-secondary-600 dark:text-secondary-300 max-w-3xl mx-auto mb-8">
            My professional journey, education, and technical expertise.
          </p>
          <button className="btn-primary flex items-center gap-2 mx-auto">
            <Download size={20} />
            Download Full Resume
          </button>
        </motion.div>

        {/* Tab Navigation */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="flex flex-wrap justify-center gap-4 mb-12"
        >
          <button
            onClick={() => setActiveTab('experience')}
            className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
              activeTab === 'experience'
                ? 'bg-primary-600 text-white shadow-lg'
                : 'bg-white dark:bg-dark-800 text-secondary-600 dark:text-secondary-300 hover:bg-primary-50 dark:hover:bg-primary-900/20 hover:text-primary-600 dark:hover:text-primary-400'
            }`}
          >
            <Briefcase size={20} />
            Experience
          </button>
          <button
            onClick={() => setActiveTab('education')}
            className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
              activeTab === 'education'
                ? 'bg-primary-600 text-white shadow-lg'
                : 'bg-white dark:bg-dark-800 text-secondary-600 dark:text-secondary-300 hover:bg-primary-50 dark:hover:bg-primary-900/20 hover:text-primary-600 dark:hover:text-primary-400'
            }`}
          >
            <GraduationCap size={20} />
            Education
          </button>
          <button
            onClick={() => setActiveTab('skills')}
            className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
              activeTab === 'skills'
                ? 'bg-primary-600 text-white shadow-lg'
                : 'bg-white dark:bg-dark-800 text-secondary-600 dark:text-secondary-300 hover:bg-primary-50 dark:hover:bg-primary-900/20 hover:text-primary-600 dark:hover:text-primary-400'
            }`}
          >
            <Star size={20} />
            Skills
          </button>
        </motion.div>

        {/* Tab Content */}
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          {activeTab === 'experience' && (
            <div className="space-y-8">
              {experiences.map((exp, index) => (
                <motion.div
                  key={exp.id}
                  initial={{ opacity: 0, x: -50 }}
                  animate={inView ? { opacity: 1, x: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                  className="bg-white dark:bg-dark-800 rounded-xl p-8 shadow-lg hover:shadow-xl dark:shadow-dark-900/50 transition-all duration-300"
                >
                  <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-4">
                    <div>
                      <h3 className="text-2xl font-bold text-secondary-900 dark:text-white mb-2">
                        {exp.title}
                      </h3>
                      <p className="text-lg text-primary-600 font-medium mb-2">
                        {exp.company}
                      </p>
                      <div className="flex items-center gap-4 text-secondary-500 dark:text-secondary-400">
                        <div className="flex items-center gap-1">
                          <MapPin size={16} />
                          {exp.location}
                        </div>
                        <div className="flex items-center gap-1">
                          <Calendar size={16} />
                          {exp.period}
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <ul className="space-y-2 mb-6">
                    {exp.description.map((item, idx) => (
                      <li key={idx} className="flex items-start gap-3">
                        <div className="w-2 h-2 bg-primary-600 rounded-full mt-2 flex-shrink-0"></div>
                        <span className="text-secondary-600 dark:text-secondary-300">{item}</span>
                      </li>
                    ))}
                  </ul>
                  
                  <div className="flex flex-wrap gap-2">
                    {exp.technologies.map((tech) => (
                      <span
                        key={tech}
                        className="px-3 py-1 bg-primary-100 dark:bg-primary-900/30 text-primary-700 dark:text-primary-300 rounded-full text-sm font-medium"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {activeTab === 'education' && (
            <div className="space-y-8">
              {education.map((edu, index) => (
                <motion.div
                  key={edu.id}
                  initial={{ opacity: 0, x: -50 }}
                  animate={inView ? { opacity: 1, x: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                  className="bg-white dark:bg-dark-800 rounded-xl p-8 shadow-lg hover:shadow-xl dark:shadow-dark-900/50 transition-all duration-300"
                >
                  <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-4">
                    <div>
                      <h3 className="text-2xl font-bold text-secondary-900 dark:text-white mb-2">
                        {edu.degree}
                      </h3>
                      <p className="text-lg text-primary-600 font-medium mb-2">
                        {edu.institution}
                      </p>
                      <div className="flex items-center gap-4 text-secondary-500 dark:text-secondary-400">
                        <div className="flex items-center gap-1">
                          <MapPin size={16} />
                          {edu.location}
                        </div>
                        <div className="flex items-center gap-1">
                          <Calendar size={16} />
                          {edu.period}
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <p className="text-secondary-600 dark:text-secondary-300 leading-relaxed">
                    {edu.description}
                  </p>
                </motion.div>
              ))}
            </div>
          )}

          {activeTab === 'skills' && (
            <div className="space-y-8">
              {skillCategories.map((category, index) => (
                <motion.div
                  key={category.category}
                  initial={{ opacity: 0, y: 30 }}
                  animate={inView ? { opacity: 1, y: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                  className="bg-white dark:bg-dark-800 rounded-xl p-8 shadow-lg dark:shadow-dark-900/50"
                >
                  <h3 className="text-2xl font-bold text-secondary-900 dark:text-white mb-6">
                    {category.category}
                  </h3>
                  <div className="space-y-4">
                    {category.skills.map((skill) => (
                      <div key={skill.name}>
                        <div className="flex justify-between items-center mb-2">
                          <span className="font-medium text-secondary-700 dark:text-secondary-300">
                            {skill.name}
                          </span>
                          <span className="text-sm text-secondary-500 dark:text-secondary-400">
                            {skill.level}%
                          </span>
                        </div>
                        <div className="w-full bg-secondary-200 dark:bg-dark-700 rounded-full h-2">
                          <motion.div
                            initial={{ width: 0 }}
                            animate={inView ? { width: `${skill.level}%` } : {}}
                            transition={{ duration: 1, delay: 0.8 + index * 0.1 }}
                            className="bg-primary-600 h-2 rounded-full"
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </motion.div>
              ))}
            </div>
          )}
        </motion.div>
      </div>
    </section>
  );
};

export default Resume;
