import React, { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import CreativeWorks from './components/CreativeWorks';
import Certifications from './components/Certifications';
import Resume from './components/Resume';
import Contact from './components/Contact';
import Footer from './components/Footer';
import { BackgroundGradientAnimation } from './ui/background-gradient-animation';

const App: React.FC = () => {
  useEffect(() => {
    // Initialize EmailJS
    emailjs.init("05vg-yZOLtTcjt36N");

    // Set dark mode as default
    if (!localStorage.getItem('theme')) {
      localStorage.setItem('theme', 'dark');
    }

    const theme = localStorage.getItem('theme');
    if (theme === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  // Debug: sample points for background gradient overlay
  const [debugSamplePoints, setDebugSamplePoints] = useState<{ x: number; y: number }[]>([]);
  // Toggle for background debug overlay
  const [showDebugSamplePoints, setShowDebugSamplePoints] = useState(false); // default off
  // Toggle for glass overlays in About section
  const [disableGlassOverlays, setDisableGlassOverlays] = useState(true); // default off
  // Toggle for interactive mouse-following background gradient
  const [bgInteractive, setBGInteractive] = useState(true);
  // Helper to track document height for debug overlay
  const [docHeight, setDocHeight] = useState(0);
  useEffect(() => {
    function updateHeight() {
      setDocHeight(document.documentElement.scrollHeight);
    }
    updateHeight();
    window.addEventListener('resize', updateHeight);
    window.addEventListener('scroll', updateHeight);
    return () => {
      window.removeEventListener('resize', updateHeight);
      window.removeEventListener('scroll', updateHeight);
    };
  }, []);

  // Floating color picker state
  const [pickerPos, setPickerPos] = useState({ x: 0, y: 0 });
  const [pickerColor, setPickerColor] = useState<string>('');
  const bgColorGetterRef = useRef<((x: number, y: number) => string) | null>(null);

  // Handler to update color picker position and color
  useEffect(() => {
    function handleMouseMove(e: MouseEvent) {
      setPickerPos({ x: e.clientX, y: e.clientY });
      if (bgColorGetterRef.current) {
        const docX = e.clientX + window.scrollX;
        const docY = e.clientY + window.scrollY;
        const color = bgColorGetterRef.current(docX, docY);
        setPickerColor(color);
        // Debug log
        console.log('[ColorPicker] Mouse:', { clientX: e.clientX, clientY: e.clientY, docX, docY, color });
      }
    }
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Provide a callback to BackgroundGradientAnimation to expose getBackgroundColorAt
  function handleBGColorGetter(getter: (x: number, y: number) => string) {
    bgColorGetterRef.current = getter;
  }

  return (
    <div className="min-h-screen bg-white dark:bg-dark-950 transition-colors duration-300">
      <Header />
      <main>
        {/* Debug toggle button */}
        <button
          style={{ position: 'fixed', top: 12, left: 12, zIndex: 200 }}
          className="bg-gray-800 text-white px-3 py-1 rounded shadow text-xs opacity-80 hover:opacity-100"
          onClick={() => setShowDebugSamplePoints(v => !v)}
        >
          {showDebugSamplePoints ? 'Hide' : 'Show'} BG Sample Dots
        </button>
        {/* Glass overlay toggle button */}
        <button
          style={{ position: 'fixed', top: 48, left: 12, zIndex: 200 }}
          className="bg-gray-800 text-white px-3 py-1 rounded shadow text-xs opacity-80 hover:opacity-100 mt-2"
          onClick={() => setDisableGlassOverlays(v => !v)}
        >
          {disableGlassOverlays ? 'Enable' : 'Disable'} About Glass Overlay
        </button>
        {/* Mouse-following background gradient toggle button */}
        <button
          style={{ position: 'fixed', top: 84, left: 12, zIndex: 200 }}
          className="bg-gray-800 text-white px-3 py-1 rounded shadow text-xs opacity-80 hover:opacity-100 mt-2"
          onClick={() => setBGInteractive(v => !v)}
        >
          {bgInteractive ? 'Disable' : 'Enable'} BG Mouse Effect
        </button>
        {/* Shared gradient and glass overlay for Hero + About */}
        <div className="relative">
          <div className="absolute inset-0 z-0">
            <BackgroundGradientAnimation
              gradientBackgroundStart="rgb(226, 232, 240)"
              gradientBackgroundEnd="rgb(203, 213, 225)"
              firstColor="37, 99, 235"
              secondColor="126, 34, 206"
              thirdColor="6, 182, 212"
              fourthColor="190, 24, 93"
              fifthColor="21, 128, 61"
              pointerColor="37, 99, 235"
              size="50%"
              blendingValue="soft-light"
              interactive={bgInteractive}
              debugSamplePoints={debugSamplePoints}
              showDebugSamplePoints={showDebugSamplePoints}
              onExposeGetColor={handleBGColorGetter}
            />
          </div>
          <div className="relative z-10 pointer-events-none">
            {/* Hero Section */}
            <div className="relative" style={{ minHeight: '80vh' }}>
              {!disableGlassOverlays && (
                <div className="absolute inset-0 z-0 pointer-events-none bg-white/70 dark:bg-dark-900/70 backdrop-blur-lg" />
              )}
              <Hero />
            </div>
            {/* Glass transition section with About anchor in the middle */}
            <div className="relative" style={{ height: '10rem' }}>
              <div id="about" style={{ position: 'absolute', top: '50%', left: 0, width: '100%', height: 0 }} />
              <div
                className="absolute inset-0 z-0 pointer-events-none h-full backdrop-blur-sm dark:hidden"
                style={{
                  background: 'linear-gradient(to bottom, rgba(255,255,255,0.7) 0%, rgba(255,255,255,0.7) 100%)'
                }}
              />
              <div
                className="absolute inset-0 z-0 pointer-events-none h-full backdrop-blur-sm hidden dark:block"
                style={{
                  background: 'linear-gradient(to bottom, rgba(15,23,42,0.7) 0%, rgba(22,32,50,0.7) 50%, rgba(30,41,59,0.7) 100%)'
                }}
              />
            </div>
            {/* About Section */}
            <div className="relative" style={{ minHeight: '80vh' }}>
              {!disableGlassOverlays && (
                <div className="absolute inset-0 z-0 pointer-events-none bg-white/70 dark:bg-dark-800/70 backdrop-blur-lg" />
              )}
              <About onDebugSamplePointsChange={setDebugSamplePoints} disableGlassOverlays={disableGlassOverlays} />
            </div>
          </div>
        </div>
        {/* Other sections remain outside the shared gradient */}
        <CreativeWorks />
        <Certifications />
        <Resume />
        <Contact />
      </main>
      <Footer />
      {/* Floating color picker */}
      {bgColorGetterRef.current && (
        <div
          style={{
            position: 'fixed',
            left: pickerPos.x + 16,
            top: pickerPos.y + 16,
            zIndex: 3000,
            background: '#fff',
            border: '1px solid #ccc',
            borderRadius: 8,
            padding: '8px 12px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.12)',
            display: 'flex',
            alignItems: 'center',
            pointerEvents: 'none',
            minWidth: 120,
          }}
        >
          <span
            style={{
              display: 'inline-block',
              width: 28,
              height: 28,
              borderRadius: 6,
              background: pickerColor,
              border: '1px solid #bbb',
              marginRight: 10,
            }}
          />
          <span style={{ fontFamily: 'monospace', fontSize: 14 }}>{pickerColor}</span>
        </div>
      )}
    </div>
  );
};

export default App;
