import React, { useRef, useState, useLayoutEffect } from 'react';
import { useBackgroundColorsAtPoints } from '../hooks/useBackgroundColorsAtPoints';

interface GlassButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  children: React.ReactNode;
  bgColor?: string; // e.g. 'rgba(37,99,235,0.7)' or 'rgba(241,245,249,0.7)'
  textColor?: string;
  borderColor?: string;
  shadow?: string;
  cornerGlowStrength?: number; // 0.0 - 1.0, multiplies alpha of corner glows
  cornerGlowOpacity?: number; // 0.0 - 1.0, base alpha for the glow (default 1)
  cornerGlowBlur?: number; // px value for blur (default 6)
  className?: string;
}

const GlassButton: React.FC<GlassButtonProps> = ({
  children,
  bgColor = 'rgba(241,245,249,0.7)', // default: same as glass containers
  textColor = '#fff',
  borderColor = 'rgba(226,232,240,0.4)',
  shadow = '0 4px 24px 0 rgba(30,41,59,0.10), 0 1.5px 8px 0 rgba(255,255,255,0.10), 0 0.5px 2px 0 rgba(255,255,255,0.07)',
  cornerGlowStrength = 1,
  cornerGlowOpacity = 1,
  cornerGlowBlur = 6,
  className = '',
  ...props
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [shine, setShine] = useState({ x: 0, y: 0 });
  const btnRef = useRef<HTMLButtonElement>(null);

  // Sample background color at eight unique edge points: two per edge (quarter and three-quarters along each edge)
  const [edgePoints, setEdgePoints] = useState<{x: number, y: number}[]>([
    {x: 0, y: 0}, // top-left quarter
    {x: 0, y: 0}, // top-right quarter
    {x: 0, y: 0}, // right-top quarter
    {x: 0, y: 0}, // right-bottom quarter
    {x: 0, y: 0}, // bottom-right quarter
    {x: 0, y: 0}, // bottom-left quarter
    {x: 0, y: 0}, // left-bottom quarter
    {x: 0, y: 0}, // left-top quarter
  ]);
  useLayoutEffect(() => {
    if (!btnRef.current) return;
    const rect = btnRef.current.getBoundingClientRect();
    setEdgePoints([
      // Top edge: 1/4 and 3/4
      { x: rect.left + rect.width * 0.25, y: rect.top + 4 },
      { x: rect.left + rect.width * 0.75, y: rect.top + 4 },
      // Right edge: 1/4 and 3/4
      { x: rect.right - 4, y: rect.top + rect.height * 0.25 },
      { x: rect.right - 4, y: rect.top + rect.height * 0.75 },
      // Bottom edge: 3/4 and 1/4 (to keep order consistent)
      { x: rect.left + rect.width * 0.75, y: rect.bottom - 4 },
      { x: rect.left + rect.width * 0.25, y: rect.bottom - 4 },
      // Left edge: 3/4 and 1/4
      { x: rect.left + 4, y: rect.top + rect.height * 0.75 },
      { x: rect.left + 4, y: rect.top + rect.height * 0.25 },
    ]);
  }, [children, className]);
  const [
    autoTintTopLeftQuarter,
    autoTintTopRightQuarter,
    autoTintRightTopQuarter,
    autoTintRightBottomQuarter,
    autoTintBottomRightQuarter,
    autoTintBottomLeftQuarter,
    autoTintLeftBottomQuarter,
    autoTintLeftTopQuarter
  ] = useBackgroundColorsAtPoints(edgePoints, 60);

  // Helper to blend with white and darken a bit (like GlassContainer)
  function blendWhiteTint(alpha: number, tintOverride?: string) {
    let tint = tintOverride;
    if (!tint || tint === 'rgba(255,255,255,0.0)' || tint === 'transparent') {
      return `rgba(255,255,255,${alpha})`;
    }
    const match = tint.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)/);
    if (!match) return `rgba(255,255,255,${alpha})`;
    // 20% white, 80% tint, then darken 10%
    const r = Math.round((0.2 * 255 + 0.8 * parseInt(match[1])) * 0.9);
    const g = Math.round((0.2 * 255 + 0.8 * parseInt(match[2])) * 0.9);
    const b = Math.round((0.2 * 255 + 0.8 * parseInt(match[3])) * 0.9);
    return `rgba(${r},${g},${b},${alpha})`;
  }

  // Corner glow alpha scaling (strength * opacity) - use neutral white for consistency
  const topLeftGlow = `radial-gradient(circle at -20% -20%, rgba(255,255,255,${0.36 * cornerGlowStrength * cornerGlowOpacity}) 0%, rgba(255,255,255,${0.16 * cornerGlowStrength * cornerGlowOpacity}) 60%, rgba(255,255,255,0.0) 100%)`;
  const bottomRightGlow = `radial-gradient(circle at 120% 120%, rgba(255,255,255,${0.28 * cornerGlowStrength * cornerGlowOpacity}) 0%, rgba(255,255,255,${0.12 * cornerGlowStrength * cornerGlowOpacity}) 60%, rgba(255,255,255,0.0) 100%)`;

  // Use neutral white for inner shadow consistency
  const edgeShadow = `
    inset 0 2px 8px 0 rgba(255,255,255,0.12),
    inset -2px 0 8px 0 rgba(255,255,255,0.12),
    inset 0 -2px 8px 0 rgba(255,255,255,0.12),
    inset 2px 0 8px 0 rgba(255,255,255,0.12)
  `;

  return (
    <button
      ref={btnRef}
      className={`relative group overflow-hidden pointer-events-auto py-3 px-6 rounded-lg font-medium focus:outline-none focus:ring-0 transition-all duration-300 flex items-center gap-2 ${className}`}
      style={{
        background: bgColor,
        color: textColor,
        border: `1px solid ${borderColor}`,
        boxShadow: shadow,
        backdropFilter: 'blur(20px)',
        WebkitBackdropFilter: 'blur(20px)',
        position: 'relative',
      }}
      onMouseMove={e => {
        const rect = btnRef.current?.getBoundingClientRect();
        if (rect) {
          setShine({
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
          });
        }
        setIsHovered(true);
      }}
      onMouseLeave={() => setIsHovered(false)}
      {...props}
    >
      {/* Persistent edge-hugging highlights */}
      <span
        className="pointer-events-none absolute left-0 top-0 w-[32px] h-[32px]"
        style={{
          left: '-10px',
          top: '-10px',
          background: topLeftGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      <span
        className="pointer-events-none absolute right-0 bottom-0 w-[32px] h-[32px]"
        style={{
          right: '-10px',
          bottom: '-10px',
          background: bottomRightGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      {/* Mouse-following shine */}
      {isHovered && (
        <span
          className="pointer-events-none absolute top-0 left-0 h-full w-full"
          style={{
            background: `radial-gradient(circle at ${shine.x}px ${shine.y}px, rgba(255,255,255,0.18) 0%, rgba(255,255,255,0.09) 40%, rgba(255,255,255,0.0) 70%)`,
            filter: 'blur(2.5px)',
            zIndex: 2,
            transition: 'background 0.15s',
          }}
          aria-hidden="true"
        />
      )}
      {/* Inner shadow (inset) */}
      <span
        className="pointer-events-none absolute inset-0 rounded-lg"
        style={{
          boxShadow: edgeShadow,
          zIndex: 2,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      {/* Button content */}
      <span className="relative z-20 flex items-center gap-2">{children}</span>
    </button>
  );
};

export default GlassButton;
