import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Award, Calendar, ExternalLink, CheckCircle } from 'lucide-react';

interface Certification {
  id: number;
  name: string;
  issuer: string;
  date: string;
  credentialId: string;
  image: string;
  url?: string;
  category: 'development' | 'design' | 'cloud' | 'other';
  featured: boolean;
}

const Certifications: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const certifications: Certification[] = [
    {
      id: 1,
      name: "AWS Certified Solutions Architect",
      issuer: "Amazon Web Services",
      date: "2023",
      credentialId: "AWS-123456",
      image: "https://images.unsplash.com/photo-1451187580459-43490279c0fa?w=400&h=300&fit=crop",
      url: "https://aws.amazon.com/certification/",
      category: "cloud",
      featured: true
    },
    {
      id: 2,
      name: "Google Cloud Professional Developer",
      issuer: "Google Cloud",
      date: "2023",
      credentialId: "GCP-789012",
      image: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=300&fit=crop",
      url: "https://cloud.google.com/certification",
      category: "cloud",
      featured: true
    },
    {
      id: 3,
      name: "React Developer Certification",
      issuer: "Meta",
      date: "2022",
      credentialId: "META-345678",
      image: "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=400&h=300&fit=crop",
      url: "https://www.meta.com/developers/",
      category: "development",
      featured: true
    },
    {
      id: 4,
      name: "UI/UX Design Professional",
      issuer: "Interaction Design Foundation",
      date: "2022",
      credentialId: "IDF-901234",
      image: "https://images.unsplash.com/photo-1561070791-2526d30994b5?w=400&h=300&fit=crop",
      url: "https://www.interaction-design.org/",
      category: "design",
      featured: false
    },
    {
      id: 5,
      name: "Node.js Developer Certification",
      issuer: "OpenJS Foundation",
      date: "2021",
      credentialId: "OJS-567890",
      image: "https://images.unsplash.com/photo-1555066931-4365d14bab8c?w=400&h=300&fit=crop",
      url: "https://openjsf.org/",
      category: "development",
      featured: false
    },
    {
      id: 6,
      name: "Docker Certified Associate",
      issuer: "Docker Inc.",
      date: "2021",
      credentialId: "DCA-123789",
      image: "https://images.unsplash.com/photo-1605745341112-85968b19335b?w=400&h=300&fit=crop",
      url: "https://www.docker.com/",
      category: "development",
      featured: false
    }
  ];

  const categories = [
    { id: 'all', label: 'All Certifications', count: certifications.length },
    { id: 'development', label: 'Development', count: certifications.filter(c => c.category === 'development').length },
    { id: 'cloud', label: 'Cloud & DevOps', count: certifications.filter(c => c.category === 'cloud').length },
    { id: 'design', label: 'Design', count: certifications.filter(c => c.category === 'design').length },
  ];

  return (
    <section id="certifications" className="section-padding bg-white dark:bg-dark-900">
      <div className="container-max">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-secondary-900 dark:text-white mb-4">
            Certifications & Achievements
          </h2>
          <p className="text-xl text-secondary-600 dark:text-secondary-300 max-w-3xl mx-auto">
            Professional certifications that validate my expertise and commitment to continuous learning.
          </p>
        </motion.div>

        {/* Featured Certifications */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="mb-16"
        >
          <h3 className="text-2xl font-bold text-secondary-900 dark:text-white text-center mb-8">
            Featured Certifications
          </h3>
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {certifications.filter(c => c.featured).map((cert, index) => (
              <motion.div
                key={cert.id}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                className="bg-gradient-to-br from-primary-50 to-secondary-50 dark:from-dark-800 dark:to-dark-700 rounded-xl p-6 shadow-lg hover:shadow-xl dark:shadow-dark-900/50 transition-all duration-300 card-hover"
              >
                <div className="flex items-center justify-between mb-4">
                  <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center">
                    <Award className="w-6 h-6 text-primary-600" />
                  </div>
                  {cert.url && (
                    <a
                      href={cert.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-primary-600 hover:text-primary-700 transition-colors duration-200"
                    >
                      <ExternalLink size={20} />
                    </a>
                  )}
                </div>
                
                <h4 className="text-lg font-bold text-secondary-900 dark:text-white mb-2">
                  {cert.name}
                </h4>
                <p className="text-secondary-600 dark:text-secondary-300 mb-3">
                  {cert.issuer}
                </p>
                
                <div className="flex items-center gap-4 text-sm text-secondary-500 dark:text-secondary-400 mb-4">
                  <div className="flex items-center gap-1">
                    <Calendar size={16} />
                    {cert.date}
                  </div>
                  <div className="flex items-center gap-1">
                    <CheckCircle size={16} />
                    {cert.credentialId}
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <span className="px-3 py-1 bg-primary-100 text-primary-700 rounded-full text-sm font-medium capitalize">
                    {cert.category}
                  </span>
                  <span className="text-xs text-secondary-500 dark:text-secondary-400">
                    Featured
                  </span>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* All Certifications */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <h3 className="text-2xl font-bold text-secondary-900 dark:text-white text-center mb-8">
            All Certifications
          </h3>
          
          {/* Category Stats */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-12">
            {categories.map((category) => (
              <div key={category.id} className="text-center p-4 bg-secondary-50 dark:bg-dark-800 rounded-lg">
                <div className="text-2xl font-bold text-primary-600 mb-1">
                  {category.count}
                </div>
                <div className="text-sm text-secondary-600 dark:text-secondary-300">
                  {category.label}
                </div>
              </div>
            ))}
          </div>

          {/* Certifications Grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {certifications.map((cert, index) => (
              <motion.div
                key={cert.id}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.8 + index * 0.1 }}
                className="bg-white dark:bg-dark-800 rounded-lg p-6 shadow-md hover:shadow-lg dark:shadow-dark-900/50 transition-all duration-300 border border-secondary-100 dark:border-dark-700"
              >
                <div className="flex items-center justify-between mb-4">
                  <div className="w-10 h-10 bg-secondary-100 dark:bg-dark-700 rounded-lg flex items-center justify-center">
                    <Award className="w-5 h-5 text-secondary-600 dark:text-secondary-400" />
                  </div>
                  {cert.url && (
                    <a
                      href={cert.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-secondary-400 hover:text-primary-600 transition-colors duration-200"
                    >
                      <ExternalLink size={16} />
                    </a>
                  )}
                </div>
                
                <h4 className="font-bold text-secondary-900 mb-2">
                  {cert.name}
                </h4>
                <p className="text-sm text-secondary-600 mb-3">
                  {cert.issuer}
                </p>
                
                <div className="flex items-center justify-between text-xs text-secondary-500 mb-3">
                  <span>{cert.date}</span>
                  <span>{cert.credentialId}</span>
                </div>

                <span className="px-2 py-1 bg-secondary-100 text-secondary-700 rounded-full text-xs font-medium capitalize">
                  {cert.category}
                </span>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.2 }}
          className="text-center mt-16"
        >
          <p className="text-lg text-secondary-600 mb-6">
            Continuously expanding my knowledge and skills through professional development.
          </p>
          <button className="btn-secondary">
            View All Credentials
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default Certifications;
