import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { ExternalLink, Github, Code, Palette, Globe } from 'lucide-react';
import SpotlightGlow from '../ui/SpotlightGlow';

interface GitHubRepo {
  id: number;
  name: string;
  description: string | null;
  html_url: string;
  homepage: string | null;
  language: string | null;
  topics: string[];
  fork: boolean;
  archived: boolean;
  stargazers_count: number;
  updated_at: string;
  has_pages?: boolean;
  default_branch: string;
}

interface Project {
  id: number;
  title: string;
  description: string;
  image: string;
  category: 'web' | 'design' | 'other';
  technologies: string[];
  liveUrl?: string;
  githubUrl: string;
  featured: boolean;
  stars: number;
  updatedAt: string;
}

// Procedural image generator based on title/description seeds
function hashString(str: string): number {
  let h = 2166136261;
  for (let i = 0; i < str.length; i++) {
    h ^= str.charCodeAt(i);
    h += (h << 1) + (h << 4) + (h << 7) + (h << 8) + (h << 24);
  }
  return h >>> 0;
}

function seededRandom(seed: number) {
  let s = seed >>> 0;
  return () => {
    // xorshift32
    s ^= s << 13; s ^= s >>> 17; s ^= s << 5;
    return ((s >>> 0) % 10000) / 10000;
  };
}

const LIGHT_PALETTE = [
  'hsl(221, 83%, 53%)',
  'hsl(259, 94%, 61%)',
  'hsl(280, 83%, 60%)',
  'hsl(199, 89%, 48%)',
  'hsl(173, 80%, 40%)',
  'hsl(14, 90%, 57%)',
  'hsl(340, 82%, 52%)',
  'hsl(234, 14%, 35%)',
];
const DARK_PALETTE = [
  'hsl(221, 83%, 66%)',
  'hsl(259, 94%, 70%)',
  'hsl(280, 83%, 72%)',
  'hsl(199, 95%, 72%)',
  'hsl(173, 80%, 65%)',
  'hsl(14, 90%, 68%)',
  'hsl(340, 82%, 68%)',
  'hsl(234, 14%, 70%)',
];

function generateProceduralImage(seedTitle: string, seedDesc: string = '', width = 800, height = 480): string {
  const seed = hashString(seedTitle + '|' + seedDesc);
  const rand = seededRandom(seed);
  const isDark = typeof document !== 'undefined' && document.documentElement.classList.contains('dark');
  const palette = isDark ? DARK_PALETTE : LIGHT_PALETTE;
  const idx1 = Math.floor(rand() * palette.length);
  const idx2 = (idx1 + 2 + Math.floor(rand() * (palette.length - 1))) % palette.length;
  const grad1 = palette[idx1];
  const grad2 = palette[idx2];
  const bg = isDark ? 'hsl(222, 47%, 11%)' : 'hsl(210, 20%, 96%)';

  // Seed-dictated mirror complexity: 0 = 2-way, 1 = 4-way, 2 = 8-way (approx)
  const mirrorMode = Math.floor(rand() * 3); // 0..2

  // Generate base shapes ONLY in the upper-left quadrant
  const qWidth = Math.floor(width / 2);
  const qHeight = Math.floor(height / 2);

  // Circles (blurred fill)
  const circles: string[] = [];
  const circleCount = 6 + Math.floor(rand() * 5);
  for (let i = 0; i < circleCount; i++) {
    const cx = Math.floor(rand() * qWidth);
    const cy = Math.floor(rand() * qHeight);
    const r = Math.floor(40 + rand() * 140);
    const colorIdx = Math.floor(rand() * palette.length);
    const fill = palette[(idx1 + colorIdx) % palette.length];
    const opacity = (isDark ? 0.22 : 0.18) + rand() * (isDark ? 0.30 : 0.26);
    circles.push(`<circle cx="${cx}" cy="${cy}" r="${r}" fill="${fill}" opacity="${opacity.toFixed(2)}" />`);
  }

  // Rings (stroke-only)
  const rings: string[] = [];
  const ringCount = 3 + Math.floor(rand() * 4);
  for (let i = 0; i < ringCount; i++) {
    const cx = Math.floor(rand() * qWidth);
    const cy = Math.floor(rand() * qHeight);
    const r = Math.floor(60 + rand() * 160);
    const colorIdx = Math.floor(rand() * palette.length);
    const stroke = palette[(idx2 + colorIdx) % palette.length];
    const strokeOpacity = (isDark ? 0.18 : 0.12) + rand() * 0.18;
    const strokeWidth = 2 + Math.floor(rand() * 4);
    rings.push(`<circle cx="${cx}" cy="${cy}" r="${r}" fill="none" stroke="${stroke}" stroke-opacity="${strokeOpacity.toFixed(2)}" stroke-width="${strokeWidth}" />`);
  }

  // Rotated rounded rectangles
  const rects: string[] = [];
  const rectCount = 4 + Math.floor(rand() * 4);
  for (let i = 0; i < rectCount; i++) {
    const w = Math.floor(80 + rand() * 180);
    const h = Math.floor(40 + rand() * 120);
    const x = Math.floor(rand() * Math.max(1, qWidth - w));
    const y = Math.floor(rand() * Math.max(1, qHeight - h));
    const rx = 10 + Math.floor(rand() * 24);
    const angle = Math.floor(rand() * 360);
    const colorIdx = Math.floor(rand() * palette.length);
    const fill = palette[(idx1 + colorIdx * 2) % palette.length];
    const opacity = (isDark ? 0.14 : 0.10) + rand() * 0.18;
    rects.push(`<g transform="rotate(${angle} ${x + w / 2} ${y + h / 2})"><rect x="${x}" y="${y}" width="${w}" height="${h}" rx="${rx}" ry="${rx}" fill="${fill}" opacity="${opacity.toFixed(2)}" /></g>`);
  }

  // Triangles
  const tris: string[] = [];
  const triCount = 3 + Math.floor(rand() * 3);
  for (let i = 0; i < triCount; i++) {
    const x1 = Math.floor(rand() * qWidth), y1 = Math.floor(rand() * qHeight);
    const x2 = Math.floor(rand() * qWidth), y2 = Math.floor(rand() * qHeight);
    const x3 = Math.floor(rand() * qWidth), y3 = Math.floor(rand() * qHeight);
    const colorIdx = Math.floor(rand() * palette.length);
    const fill = palette[(idx2 + colorIdx * 3) % palette.length];
    const opacity = (isDark ? 0.12 : 0.09) + rand() * 0.15;
    tris.push(`<polygon points="${x1},${y1} ${x2},${y2} ${x3},${y3}" fill="${fill}" opacity="${opacity.toFixed(2)}" />`);
  }

  // Subtle diagonal stripes overlay
  const stripes = [] as string[];
  const stripeCount = 12;
  for (let i = 0; i < stripeCount; i++) {
    const y = Math.floor((i / stripeCount) * height);
    const alpha = (isDark ? 0.06 : 0.04) + (i % 2 === 0 ? (isDark ? 0.02 : 0.015) : 0);
    const stripeColor = isDark ? 'white' : 'black';
    stripes.push(`<rect x="0" y="${y}" width="${width}" height="${Math.ceil(height / stripeCount)}" fill="${stripeColor}" opacity="${alpha.toFixed(2)}" />`);
  }

  // Base group for one quadrant
  const baseGroup = [
    `<g filter=\"url(#blurMe)\">${circles.join('\\n')}</g>`,
    `<g>${rings.join('\\n')}</g>`,
    `<g filter=\"url(#blurMe)\">${rects.join('\\n')}</g>`,
    `<g>${tris.join('\\n')}</g>`
  ].join('');

  // Mirror transforms based on mode
  const mirrors: string[] = [];
  // Always include original (upper-left)
  mirrors.push(baseGroup);
  // Horizontal mirror (upper-right)
  mirrors.push(`<g transform=\"translate(${width},0) scale(-1,1)\">${baseGroup}</g>`);
  // Vertical mirrors
  mirrors.push(`<g transform=\"translate(0,${height}) scale(1,-1)\">${baseGroup}</g>`);
  mirrors.push(`<g transform=\"translate(${width},${height}) scale(-1,-1)\">${baseGroup}</g>`);

  // Optional additional rotational symmetry (approx kaleidoscope) if mode >= 2
  if (mirrorMode >= 2) {
    const steps = 4; // add 4 rotated copies around center
    for (let i = 0; i < steps; i++) {
      const angle = (360 / steps) * i + Math.floor(rand() * 15);
      mirrors.push(`<g transform=\"translate(${width / 2},${height / 2}) rotate(${angle}) translate(${-width / 2},${-height / 2})\">${baseGroup}</g>`);
    }
  }

  const svg = `<?xml version=\"1.0\" encoding=\"UTF-8\"?>
  <svg xmlns=\"http://www.w3.org/2000/svg\" width=\"${width}\" height=\"${height}\" viewBox=\"0 0 ${width} ${height}\">\n    <defs>\n      <linearGradient id=\"g1\" x1=\"0\" y1=\"0\" x2=\"1\" y2=\"1\">\n        <stop offset=\"0%\" stop-color=\"${grad1}\" />\n        <stop offset=\"100%\" stop-color=\"${grad2}\" />\n      </linearGradient>\n      <filter id=\"blurMe\"><feGaussianBlur stdDeviation=\"12\" /></filter>\n    </defs>\n    <rect width=\"100%\" height=\"100%\" fill=\"${bg}\" />\n    <rect width=\"100%\" height=\"100%\" fill=\"url(#g1)\" opacity=\"${isDark ? '0.55' : '0.65'}\" />\n    ${mirrors.join('')}\n    <g opacity=\"0.35\">${stripes.join('\\n')}</g>\n  </svg>`;
  return `data:image/svg+xml;utf8,${encodeURIComponent(svg)}`;
}

const CreativeWorks: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.01,
  });

  const [activeFilter, setActiveFilter] = useState<string>('all');
  const [projects, setProjects] = useState<Project[]>([]);
  const [loading, setLoading] = useState(true);
  // Trigger re-render on theme change so procedural images update
  const [isDarkMode, setIsDarkMode] = useState(false);
  useEffect(() => {
    const check = () => setIsDarkMode(document.documentElement.classList.contains('dark'));
    check();
    const observer = new MutationObserver(check);
    observer.observe(document.documentElement, { attributes: true, attributeFilter: ['class'] });
    return () => observer.disconnect();
  }, []);

  // Function to fetch README content
  const fetchReadmeContent = async (repoName: string): Promise<string> => {
    try {
      const response = await fetch(`https://api.github.com/repos/AlecMcCutcheon/${repoName}/readme`);
      if (!response.ok) return '';

      const data = await response.json();
      const readmeContent = atob(data.content); // Decode base64 content

      // Extract first paragraph or first few sentences
      const lines = readmeContent.split('\n').filter(line => line.trim());
      let description = '';

      for (const line of lines) {
        // Skip headers, code blocks, and empty lines
        if (line.startsWith('#') || line.startsWith('```') || line.trim() === '') continue;

        // Take the first meaningful paragraph
        if (line.trim().length > 20) {
          description = line.trim();
          break;
        }
      }

      return description;
    } catch (error) {
      console.error(`Error fetching README for ${repoName}:`, error);
      return '';
    }
  };

  // Function to check if repository has GitHub Pages
  const checkGitHubPages = (repo: GitHubRepo): boolean => {
    // Use the has_pages field from the repository data
    return repo.has_pages || false;
  };

  // Function to fetch social media preview image and description from a URL using Open Graph API
  const fetchSocialData = async (url: string): Promise<{image: string, description: string}> => {
    try {
      // Use a public Open Graph API service to avoid CORS issues
      // Request higher quality images with specific dimensions
      const apiUrl = `https://api.microlink.io?url=${encodeURIComponent(url)}&meta=true&screenshot=true&screenshot.width=1200&screenshot.height=630&image.width=1200&image.height=630`;
      const response = await fetch(apiUrl);
      const data = await response.json();

      let image = "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=600&h=400&fit=crop";
      let description = "Professional website showcasing modern design and functionality.";

      if (data.status === 'success' && data.data) {
        // Try to get the Open Graph image with high quality
        if (data.data.image && data.data.image.url) {
          image = data.data.image.url;
        } else if (data.data.screenshot && data.data.screenshot.url) {
          image = data.data.screenshot.url;
        } else if (data.data.logo && data.data.logo.url) {
          image = data.data.logo.url;
        }

        // Try to get the Open Graph description
        if (data.data.description) {
          description = data.data.description;
        } else if (data.data.title) {
          description = data.data.title;
        }
      }

      return { image, description };
    } catch (error) {
      console.error(`Error fetching social data for ${url}:`, error);
      return {
        image: "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=600&h=400&fit=crop",
        description: "Professional website showcasing modern design and functionality."
      };
    }
  };

  // Function to generate a better description
  const generateDescription = (repo: GitHubRepo, readmeContent: string): string => {
    // If repo has a good description, use it
    if (repo.description && repo.description.length > 50) {
      return repo.description;
    }

    // If README content is available and meaningful, use it
