import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { ExternalLink, Github, Code, Palette, Globe } from 'lucide-react';

interface GitHubRepo {
  id: number;
  name: string;
  description: string | null;
  html_url: string;
  homepage: string | null;
  language: string | null;
  topics: string[];
  fork: boolean;
  stargazers_count: number;
  updated_at: string;
}

interface Project {
  id: number;
  title: string;
  description: string;
  image: string;
  category: 'web' | 'design' | 'other';
  technologies: string[];
  liveUrl?: string;
  githubUrl: string;
  featured: boolean;
  stars: number;
  updatedAt: string;
}

const CreativeWorks: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [activeFilter, setActiveFilter] = useState<string>('all');
  const [projects, setProjects] = useState<Project[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGitHubProjects = async () => {
      try {
        const response = await fetch('https://api.github.com/users/AlecMcCutcheon/repos');
        const repos: GitHubRepo[] = await response.json();
        
        // Filter out forks and sort by stars and update date
        const filteredRepos = repos
          .filter(repo => !repo.fork && repo.description)
          .sort((a, b) => b.stargazers_count - a.stargazers_count || new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime());

        const projectImages = [
          "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=600&h=400&fit=crop",
          "https://images.unsplash.com/photo-1561070791-2526d30994b5?w=600&h=400&fit=crop",
          "https://images.unsplash.com/photo-1454165804606-c3d57bc86b40?w=600&h=400&fit=crop",
          "https://images.unsplash.com/photo-1592210454359-9043f067919b?w=600&h=400&fit=crop",
          "https://images.unsplash.com/photo-1555066931-4365d14bab8c?w=600&h=400&fit=crop",
          "https://images.unsplash.com/photo-1605745341112-85968b19335b?w=600&h=400&fit=crop"
        ];

        const mappedProjects: Project[] = filteredRepos.slice(0, 6).map((repo, index) => {
          // Determine category based on topics, language, and description
          let category: 'web' | 'design' | 'other' = 'other';
          if (repo.topics.includes('web') || repo.topics.includes('react') || repo.topics.includes('javascript') || repo.language === 'JavaScript' || repo.language === 'TypeScript') {
            category = 'web';
          } else if (repo.topics.includes('design') || repo.topics.includes('ui') || repo.topics.includes('ux')) {
            category = 'design';
          }

          // Extract technologies from topics and language
          const technologies = [
            repo.language,
            ...repo.topics.filter(topic => 
              ['javascript', 'typescript', 'react', 'node', 'python', 'aws', 'docker', 'api', 'web', 'design'].includes(topic.toLowerCase())
            )
          ].filter(Boolean) as string[];

          return {
            id: repo.id,
            title: repo.name.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
            description: repo.description || 'A creative project showcasing innovative solutions and technical expertise.',
            image: projectImages[index % projectImages.length],
            category,
            technologies: technologies.length > 0 ? technologies : ['JavaScript'],
            liveUrl: repo.homepage || undefined,
            githubUrl: repo.html_url,
            featured: index < 3, // First 3 projects are featured
            stars: repo.stargazers_count,
            updatedAt: repo.updated_at
          };
        });

        setProjects(mappedProjects);
      } catch (error) {
        console.error('Error fetching GitHub projects:', error);
        // Fallback to sample projects if API fails
        setProjects([
          {
            id: 1,
            title: "Stremio Maine Local News",
            description: "Stremio Add-On to Watch Maine Local News Stations - A streaming media addon for local news content.",
            image: "https://images.unsplash.com/photo-1592210454359-9043f067919b?w=600&h=400&fit=crop",
            category: "web",
            technologies: ["JavaScript", "API", "Streaming"],
            liveUrl: "https://a0da031547f5-stremio-mainelocalnews.baby-beamup.club",
            githubUrl: "https://github.com/AlecMcCutcheon/stremio-mainelocalnews",
            featured: true,
            stars: 0,
            updatedAt: "2024-02-03T05:53:53Z"
          },
          {
            id: 2,
            title: "Stremio Maine Radio",
            description: "A streaming media addon for Maine radio stations, providing local audio content.",
            image: "https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=600&h=400&fit=crop",
            category: "web",
            technologies: ["JavaScript", "Audio", "Streaming"],
            liveUrl: "https://a0da031547f5-stremio-maine-radio.baby-beamup.club",
            githubUrl: "https://github.com/AlecMcCutcheon/stremio-maine-radio",
            featured: true,
            stars: 0,
            updatedAt: "2024-01-30T17:55:40Z"
          },
          {
            id: 3,
            title: "AWS Connect Addons",
            description: "AWS Connect addons and integrations for enhanced cloud communication solutions.",
            image: "https://images.unsplash.com/photo-1451187580459-43490279c0fa?w=600&h=400&fit=crop",
            category: "web",
            technologies: ["AWS", "Cloud", "JavaScript"],
            githubUrl: "https://github.com/AlecMcCutcheon/AWS-Connect-Addons",
            featured: true,
            stars: 0,
            updatedAt: "2023-09-24T22:05:35Z"
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchGitHubProjects();
  }, []);

  const filters = [
    { id: 'all', label: 'All Projects', icon: <Globe className="w-4 h-4" /> },
    { id: 'web', label: 'Web Apps', icon: <Code className="w-4 h-4" /> },
    { id: 'design', label: 'Design', icon: <Palette className="w-4 h-4" /> },
  ];

  const filteredProjects = activeFilter === 'all' 
    ? projects 
    : projects.filter(project => project.category === activeFilter);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  if (loading) {
    return (
      <section id="works" className="section-padding bg-secondary-50 dark:bg-dark-900">
        <div className="container-max">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
            <p className="mt-4 text-secondary-600 dark:text-secondary-300">Loading projects...</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="works" className="section-padding bg-secondary-50 dark:bg-dark-900">
      <div className="container-max">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-secondary-900 dark:text-white mb-4">
            Creative Works
          </h2>
          <p className="text-xl text-secondary-600 dark:text-secondary-300 max-w-3xl mx-auto">
            Explore my portfolio of projects, from web applications to design work. 
            Each project represents a unique challenge and creative solution.
          </p>
        </motion.div>

        {/* Filter Buttons */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="flex flex-wrap justify-center gap-4 mb-12"
        >
          {filters.map((filter) => (
            <button
              key={filter.id}
              onClick={() => setActiveFilter(filter.id)}
              className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                activeFilter === filter.id
                  ? 'bg-primary-600 text-white shadow-lg'
                  : 'bg-white dark:bg-dark-800 text-secondary-600 dark:text-secondary-300 hover:bg-primary-50 dark:hover:bg-primary-900/20 hover:text-primary-600 dark:hover:text-primary-400'
              }`}
            >
              {filter.icon}
              {filter.label}
            </button>
          ))}
        </motion.div>

        {/* Projects Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProjects.map((project, index) => (
            <motion.div
              key={project.id}
              initial={{ opacity: 0, y: 50 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
              className="bg-white dark:bg-dark-800 rounded-xl overflow-hidden shadow-lg hover:shadow-xl dark:shadow-dark-900/50 transition-all duration-300 card-hover"
            >
              {/* Project Image */}
              <div className="relative h-48 overflow-hidden">
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                />
                {project.featured && (
                  <div className="absolute top-4 left-4 bg-primary-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                    Featured
                  </div>
                )}
                <div className="absolute top-4 right-4 flex items-center gap-1 bg-black/50 text-white px-2 py-1 rounded-full text-sm">
                  <span>⭐</span>
                  <span>{project.stars}</span>
                </div>
                <div className="absolute inset-0 bg-black bg-opacity-0 hover:bg-opacity-20 transition-all duration-300 flex items-center justify-center">
                  <div className="opacity-0 hover:opacity-100 transition-opacity duration-300 flex gap-4">
                    {project.liveUrl && (
                      <a
                        href={project.liveUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="w-10 h-10 bg-white rounded-full flex items-center justify-center text-secondary-700 hover:text-primary-600 transition-colors duration-200"
                      >
                        <ExternalLink size={20} />
                      </a>
                    )}
                    <a
                      href={project.githubUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="w-10 h-10 bg-white rounded-full flex items-center justify-center text-secondary-700 hover:text-primary-600 transition-colors duration-200"
                    >
                      <Github size={20} />
                    </a>
                  </div>
                </div>
              </div>

              {/* Project Content */}
              <div className="p-6">
                <h3 className="text-xl font-bold text-secondary-900 dark:text-white mb-3">
                  {project.title}
                </h3>
                <p className="text-secondary-600 dark:text-secondary-300 mb-4 leading-relaxed">
                  {project.description}
                </p>
                
                {/* Technologies */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.technologies.map((tech) => (
                    <span
                      key={tech}
                      className="px-3 py-1 bg-secondary-100 dark:bg-dark-700 text-secondary-700 dark:text-secondary-300 rounded-full text-sm font-medium"
                    >
                      {tech}
                    </span>
                  ))}
                </div>

                {/* Updated Date */}
                <div className="text-xs text-secondary-500 dark:text-secondary-400 mb-4">
                  Updated: {formatDate(project.updatedAt)}
                </div>

                {/* Action Buttons */}
                <div className="flex gap-3">
                  {project.liveUrl && (
                    <a
                      href={project.liveUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex-1 bg-primary-600 hover:bg-primary-700 text-white text-center py-2 px-4 rounded-lg font-medium transition-colors duration-200"
                    >
                      View Live
                    </a>
                  )}
                  <a
                    href={project.githubUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex-1 bg-secondary-100 dark:bg-dark-700 hover:bg-secondary-200 dark:hover:bg-dark-600 text-secondary-700 dark:text-secondary-300 text-center py-2 px-4 rounded-lg font-medium transition-colors duration-200"
                  >
                    View Code
                  </a>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.8 }}
          className="text-center mt-16"
        >
          <p className="text-lg text-secondary-600 dark:text-secondary-300 mb-6">
            Interested in working together? Let's discuss your next project!
          </p>
          <button className="btn-primary">
            Get In Touch
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default CreativeWorks;
