import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Award, Calendar, ExternalLink, CheckCircle } from 'lucide-react';

interface Certification {
  id: number;
  name: string;
  issuer: string;
  date: string;
  credentialId: string;
  image: string;
  url?: string;
  category: 'development' | 'design' | 'cloud' | 'other';
  featured: boolean;
}

// Add fetchSocialData function (copied from CreativeWorks)
const fetchSocialData = async (url: string): Promise<{image: string, description: string}> => {
  try {
    const apiUrl = `https://api.microlink.io?url=${encodeURIComponent(url)}&meta=true`;
    const response = await fetch(apiUrl);
    const data = await response.json();
    let image = '';
    let description = '';
    if (data.status === 'success' && data.data) {
      if (data.data.image && data.data.image.url) {
        image = data.data.image.url;
      } else if (data.data.logo && data.data.logo.url) {
        image = data.data.logo.url;
      }
      if (data.data.description) {
        description = data.data.description;
      } else if (data.data.title) {
        description = data.data.title;
      }
    }
    return { image, description };
  } catch (error) {
    return { image: '', description: '' };
  }
};

const Certifications: React.FC = () => {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.01 });
  // State for CompTIA badge image
  const [comptiaImage, setComptiaImage] = useState<string>("");

  useEffect(() => {
    const fetchBadge = async () => {
      const { image } = await fetchSocialData("https://www.credly.com/badges/0afe650b-5181-470f-af84-fe9f88c23918/linked_in");
      if (image) setComptiaImage(image);
    };
    fetchBadge();
  }, []);

  // Split certifications into professional and achievements
  const professionalCerts = [
    {
      id: 1,
      name: "CompTIA A+ ce Certification",
      issuer: "Central Maine Community College",
      date: "Jan 2022",
      credentialId: "COMP001021438205",
      image: comptiaImage || "https://images.credly.com/images/0afe650b-5181-470f-af84-fe9f88c23918/CompTIA-A-plus-ce-certification.png",
      url: "https://www.credly.com/badges/0afe650b-5181-470f-af84-fe9f88c23918/linked_in",
      category: "Professional",
      featured: true,
      description: "Industry-standard certification demonstrating core IT skills, troubleshooting, and support expertise."
    }
  ];

  const achievements = [
    {
      id: 2,
      name: "Certified Client Pro",
      issuer: "TestOut Corporation",
      date: "Jan 2017",
      credentialId: "CCP-2017",
      image: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=300&fit=crop",
      url: "https://www.testout.com/",
      category: "Achievement",
      description: "Comprehensive certification covering Windows OS, hardware, networking, and security."
    },
    {
      id: 3,
      name: "Certified PC Pro",
      issuer: "TestOut Corporation",
      date: "Jan 2017",
      credentialId: "PCP-2017",
      image: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=300&fit=crop",
      url: "https://www.testout.com/",
      category: "Achievement",
      description: "Certification in essential computer hardware, OS, and system management skills."
    },
    {
      id: 4,
      name: "Financial Literacy EverFi",
      issuer: "Maine Financial Scholars Program",
      date: "Jan 2018",
      credentialId: "FL-2018",
      image: "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=400&h=300&fit=crop",
      url: "https://everfi.com/",
      category: "Achievement",
      description: "Practical knowledge in savings, credit, insurance, taxes, and investing."
    },
    {
      id: 5,
      name: "IT Achievement Certificates",
      issuer: "Mid-Maine Technical Center",
      date: "Jan 2016",
      credentialId: "IT-2016",
      image: "https://images.unsplash.com/photo-1461749280684-dccba630e2f6?w=400&h=300&fit=crop",
      category: "Achievement",
      description: "Recognizes foundational skills in number systems, binary math, and technical concepts."
    },
    {
      id: 6,
      name: "Safety Certificates",
      issuer: "Mid-Maine Technical Center",
      date: "Jan 2016",
      credentialId: "SAFE-2016",
      image: "https://images.unsplash.com/photo-1465101046530-73398c7f28ca?w=400&h=300&fit=crop",
      category: "Achievement",
      description: "Completion of workplace safety training, including ergonomics and safe equipment use."
    }
  ];

  const categories = [
    { id: 'all', label: 'All Certifications', count: professionalCerts.length + achievements.length },
    { id: 'professional', label: 'Professional Certifications', count: professionalCerts.length },
    { id: 'achievement', label: 'Achievements & Certificates', count: achievements.length },
  ];

  return (
    <section id="certifications" ref={ref} className="section-padding bg-blue-50 dark:bg-blue-950 bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-blue-100/60 via-blue-50/80 to-blue-50/90 dark:from-blue-900/80 dark:via-blue-950/90 dark:to-blue-950/100">
      <div className="container-max">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-secondary-900 dark:text-white mb-4">
            Certifications & Achievements
          </h2>
          <p className="text-xl text-secondary-600 dark:text-secondary-300 max-w-3xl mx-auto">
            Below are my professional certifications, as well as additional achievements and certificates earned through coursework and skills programs.
          </p>
        </motion.div>

        {/* Professional Certifications */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="mb-16"
        >
          <h3 className="text-2xl font-bold text-secondary-900 dark:text-white text-center mb-8">
            Professional Certifications
          </h3>
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            <div className="col-span-full max-w-3xl mx-auto w-full">
              {professionalCerts.map((cert, index) => (
                <motion.div
                  key={cert.id}
                  initial={{ opacity: 0, y: 30 }}
                  animate={inView ? { opacity: 1, y: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                  className="relative bg-gradient-to-br from-primary-50 to-secondary-50 dark:from-dark-800 dark:to-dark-700 rounded-xl p-8 shadow-lg hover:shadow-xl dark:shadow-dark-900/50 transition-all duration-300 card-hover flex flex-col md:flex-row items-center md:items-stretch gap-8 max-w-lg mx-auto"
                >
                 <div className="flex-shrink-0 flex items-center justify-center w-full md:w-auto">
                   <div className="w-36 h-36 rounded-xl bg-white shadow-md border border-secondary-100 dark:border-dark-700 flex items-center justify-center overflow-hidden">
                     <img
                       src={cert.image}
                       alt={cert.name + ' badge'}
                       className="w-36 h-36 object-cover"
                     />
                   </div>
                 </div>
                 <div className="flex-1 w-full flex flex-col justify-center md:pl-2 max-w-xs">
                   <div className="flex items-center justify-between mb-1">
                     <h4 className="text-lg font-bold text-secondary-900 dark:text-white">
                       {cert.name}
                     </h4>
                   </div>
                   <p className="text-secondary-600 dark:text-secondary-300 mb-1">
                     {cert.issuer}
                   </p>
                   <div className="flex items-center gap-4 text-sm text-secondary-500 dark:text-secondary-400 mb-2">
                     <div className="flex items-center gap-1">
                       <Calendar size={16} />
                       {cert.date}
                     </div>
                     <div className="flex items-center gap-1">
                       <CheckCircle size={16} />
                       {cert.credentialId}
                     </div>
                   </div>
                   <p className="text-sm text-secondary-700 dark:text-secondary-300 mb-2 max-w-xs">
                     {cert.description}
                   </p>
                   <div className="flex items-center gap-2 mt-2">
                     <span className="px-3 py-1 bg-primary-100 text-primary-700 rounded-full text-sm font-medium capitalize w-fit">
                       {cert.category}
                     </span>
                     {cert.url && (
                       <a
                         href={cert.url}
                         target="_blank"
                         rel="noopener noreferrer"
                         className="btn-primary px-3 py-1 text-sm font-medium rounded-full shadow-sm ml-1"
                       >
                         View Certification
                       </a>
                     )}
                   </div>
                   </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.div>

        {/* Achievements & Certificates */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <h3 className="text-2xl font-bold text-secondary-900 dark:text-white text-center mb-8">
            Achievements & Certificates
          </h3>
          <div className="flex flex-wrap justify-center gap-6">
            {achievements.map((cert, index) => (
              <motion.div
                key={cert.id}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.8 + index * 0.1 }}
                className="bg-white dark:bg-dark-800 rounded-lg p-6 shadow-md hover:shadow-lg dark:shadow-dark-900/50 transition-all duration-300 border border-secondary-100 dark:border-dark-700 w-80"
              >
                <div className="flex items-center mb-4">
                  <div className="w-10 h-10 bg-secondary-100 dark:bg-dark-700 rounded-lg flex items-center justify-center">
                    <Award className="w-5 h-5 text-secondary-600 dark:text-secondary-400" />
                  </div>
                </div>
                <h4 className="font-bold text-secondary-900 dark:text-white mb-2">
                  {cert.name}
                </h4>
                <p className="text-sm text-secondary-600 dark:text-secondary-300 mb-3">
                  {cert.issuer}
                </p>
                <div className="flex items-center justify-between text-xs text-secondary-500 dark:text-secondary-400 mb-3">
                  <span>{cert.date}</span>
                  <span>{cert.credentialId}</span>
                </div>
                <p className="text-xs text-secondary-700 dark:text-secondary-300 mb-2">
                  {cert.description}
                </p>
                <span className="px-2 py-1 bg-secondary-100 dark:bg-dark-700 text-secondary-700 dark:text-secondary-300 rounded-full text-xs font-medium capitalize">
                  {cert.category}
                </span>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Certifications;
