import React, { useRef, useState, useEffect, useLayoutEffect, useContext } from 'react';
import { useBackgroundColorsAtPoints } from '../hooks/useBackgroundColorsAtPoints';
import { BackgroundColorContext } from '../ui/background-gradient-animation';

interface GlassContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
  bgColor?: string;
  borderColor?: string;
  shadow?: string;
  cornerGlowStrength?: number;
  cornerGlowOpacity?: number;
  cornerGlowBlur?: number;
  innerShadow?: boolean;
  rounded?: string; // e.g. 'rounded-full', 'rounded-lg'
  tintColor?: string; // e.g. 'rgba(37,99,235,0.2)'
  className?: string;
}

const GlassContainer: React.FC<GlassContainerProps> = ({
  children,
  bgColor = 'rgba(241,245,249,0.7)',
  borderColor = 'rgba(226,232,240,0.4)',
  shadow = '0 4px 24px 0 rgba(30,41,59,0.10), 0 1.5px 8px 0 rgba(255,255,255,0.06), 0 0.5px 2px 0 rgba(255,255,255,0.03)',
  cornerGlowStrength = 1,
  cornerGlowOpacity = 0.5,
  cornerGlowBlur = 6,
  innerShadow = true,
  rounded = 'rounded-lg',
  tintColor,
  className = '',
  ...props
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [shine, setShine] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);
  // Detect dark mode
  const [isDarkMode, setIsDarkMode] = useState(false);
  useEffect(() => {
    const checkDark = () => setIsDarkMode(document.documentElement.classList.contains('dark'));
    checkDark();
    const observer = new MutationObserver(checkDark);
    observer.observe(document.documentElement, { attributes: true, attributeFilter: ['class'] });
    return () => observer.disconnect();
  }, []);

  // Use background color context for color-aware tinting
  const bgColorContext = useContext(BackgroundColorContext);
  // Sample background color at top-left and bottom-right corners on a timer
  const [cornerPoints, setCornerPoints] = useState<{x: number, y: number}[]>([{x: 0, y: 0}, {x: 0, y: 0}]);
  useLayoutEffect(() => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    setCornerPoints([
      { x: rect.left + 4, y: rect.top + 4 },
      { x: rect.right - 4, y: rect.bottom - 4 },
    ]);
  }, [children, className, rounded]);
  const [autoTintTopLeft, autoTintBottomRight] = useBackgroundColorsAtPoints(cornerPoints, 60);

  function blendWhiteTint(alpha: number, tintOverride?: string) {
    let tint = tintOverride ?? tintColor;
    if (!tint) {
      // Default: white in light mode, dark blue/gray in dark mode
      tint = isDarkMode ? 'rgba(30,41,59,0.18)' : 'rgba(255,255,255,0.0)';
    }
    if (tint === 'rgba(255,255,255,0.0)' || tint === 'transparent') {
      return `rgba(255,255,255,${alpha})`;
    }
    // Parse rgb(a) for tintColor
    const match = tint.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)/);
    if (!match) return `rgba(255,255,255,${alpha})`;
    // 40% white, 60% tint for more noticeable color
    const r = Math.round(0.4 * 255 + 0.6 * parseInt(match[1]));
    const g = Math.round(0.4 * 255 + 0.6 * parseInt(match[2]));
    const b = Math.round(0.4 * 255 + 0.6 * parseInt(match[3]));
    const a = alpha;
    return `rgba(${r},${g},${b},${a})`;
  }
  // Corner glow alpha scaling (strength * opacity)
  const topLeftGlow = `radial-gradient(circle at -20% -20%, ${blendWhiteTint(0.7 * cornerGlowStrength * cornerGlowOpacity, autoTintTopLeft)} 0%, ${blendWhiteTint(0.4 * cornerGlowStrength * cornerGlowOpacity, autoTintTopLeft)} 60%, rgba(255,255,255,0.0) 100%)`;
  const bottomRightGlow = `radial-gradient(circle at 120% 120%, ${blendWhiteTint(0.7 * cornerGlowStrength * cornerGlowOpacity, autoTintBottomRight)} 0%, ${blendWhiteTint(0.4 * cornerGlowStrength * cornerGlowOpacity, autoTintBottomRight)} 60%, rgba(255,255,255,0.0) 100%)`;

  // Debug: log tint settings while hovered
  useEffect(() => {
    let interval: NodeJS.Timeout | undefined;
    if (isHovered) {
      const log = () => {
        // Log the sampled tints and computed values for each section
        // eslint-disable-next-line no-console
        console.log('[GlassContainer] isHovered:', {
          isDarkMode,
          tintColor,
          autoTintTopLeft,
          autoTintBottomRight,
          blendWhiteTintTopLeft: blendWhiteTint(0.36, autoTintTopLeft),
          blendWhiteTintBottomRight: blendWhiteTint(0.28, autoTintBottomRight),
          boxShadow: `inset 0 1.5px 8px 0 ${blendWhiteTint(0.06, autoTintTopLeft)}, inset 0 0.5px 2px 0 ${blendWhiteTint(0.03, autoTintBottomRight)}`,
        });
      };
      log();
      interval = setInterval(log, 1000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isHovered, tintColor, isDarkMode, autoTintTopLeft, autoTintBottomRight]);

  return (
    <div
      ref={containerRef}
      className={`relative group overflow-hidden ${rounded} focus:outline-none transition-all duration-300 ${className}`}
      style={{
        background: bgColor,
        border: `1px solid ${borderColor}`,
        boxShadow: shadow,
        backdropFilter: 'blur(20px)',
        WebkitBackdropFilter: 'blur(20px)',
        position: 'relative',
      }}
      onMouseMove={e => {
        const rect = containerRef.current?.getBoundingClientRect();
        if (rect) {
          setShine({
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
          });
        }
        setIsHovered(true);
      }}
      onMouseLeave={() => setIsHovered(false)}
      {...props}
    >
      {/* Persistent edge-hugging highlights */}
      <span
        className={`pointer-events-none absolute left-0 top-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          left: '-10px',
          top: '-10px',
          background: topLeftGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      <span
        className={`pointer-events-none absolute right-0 bottom-0 w-[32px] h-[32px] ${rounded}`}
        style={{
          right: '-10px',
          bottom: '-10px',
          background: bottomRightGlow,
          filter: `blur(${cornerGlowBlur}px)`,
          zIndex: 1,
          pointerEvents: 'none',
        }}
        aria-hidden="true"
      />
      {/* Inner shadow (inset) */}
      {innerShadow && (
        <span
          className={`pointer-events-none absolute inset-0 ${rounded}`}
          style={{
            boxShadow: `inset 0 1.5px 8px 0 ${blendWhiteTint(0.18, autoTintTopLeft)}, inset 0 0.5px 2px 0 ${blendWhiteTint(0.09, autoTintBottomRight)}`,
            zIndex: 2,
            pointerEvents: 'none',
          }}
          aria-hidden="true"
        />
      )}
      {/* Mouse-following shine */}
      {isHovered && (
        <span
          className="pointer-events-none absolute top-0 left-0 h-full w-full"
          style={{
            background: `radial-gradient(circle at ${shine.x}px ${shine.y}px, rgba(255,255,255,0.09) 0%, rgba(255,255,255,0.04) 40%, rgba(255,255,255,0.0) 70%)`,
            filter: 'blur(2.5px)',
            zIndex: 2,
            transition: 'background 0.15s',
          }}
          aria-hidden="true"
        />
      )}
      {/* DEBUG: Render dots at the sampling points with their sampled color */}
      <div style={{
        position: 'fixed',
        left: cornerPoints[0].x - 6,
        top: cornerPoints[0].y - 6,
        width: 12,
        height: 12,
        borderRadius: 6,
        background: autoTintTopLeft,
        border: '2px solid black',
        zIndex: 99999,
        pointerEvents: 'none',
        boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)',
      }} />
      <div style={{
        position: 'fixed',
        left: cornerPoints[1].x - 6,
        top: cornerPoints[1].y - 6,
        width: 12,
        height: 12,
        borderRadius: 6,
        background: autoTintBottomRight,
        border: '2px solid black',
        zIndex: 99999,
        pointerEvents: 'none',
        boxShadow: '0 0 8px 2px rgba(0,0,0,0.2)',
      }} />
      {/* Container content */}
      <span className="relative z-20 flex items-center gap-2">{children}</span>
    </div>
  );
};

export default GlassContainer;
